import unittest

from cross_module_engine.contracts.work_item import WorkItem
from cross_module_engine.models.run_result import RunResult
from cross_module_engine.processors.push_processor import PushProcessor


class MockWorkItem(WorkItem):
    """Mock work item for testing."""

    def __init__(self, identifier: str, channel: str, payload: dict[str, str]):
        self._identifier = identifier
        self._channel = channel
        self._payload = payload

    @property
    def identifier(self) -> str:
        return self._identifier

    @property
    def channel(self) -> str:
        return self._channel

    @property
    def payload(self) -> dict[str, str]:
        return self._payload


class TestPushProcessor(unittest.TestCase):
    """Test PushProcessor functionality."""

    def test_push_processor_dispatches_successfully(self) -> None:
        """Test successful push notification dispatch."""
        processor = PushProcessor()
        item = MockWorkItem(
            identifier="test-123",
            channel="push",
            payload={"title": "Test", "body": "Hello"}
        )
        result = processor.process(item)
        self.assertIsInstance(result, RunResult)
        self.assertTrue(result.success)
        self.assertEqual(result.processor, "push")
        self.assertEqual(result.item_id, "test-123")
        self.assertIn("dispatched", result.details)

    def test_push_processor_does_not_support_email(self) -> None:
        """Test processor rejects non-push channels."""
        processor = PushProcessor()
        item = MockWorkItem(
            identifier="test-456",
            channel="email",
            payload={"to": "user@example.com", "subject": "Test", "body": "Hello"}
        )
        self.assertFalse(processor.supports(item))


if __name__ == "__main__":
    unittest.main()
