class TestPushProcessor(unittest.TestCase):
    """Test PushProcessor functionality."""

    def setUp(self) -> None:
        self.processor = PushProcessor()

    def test_push_processor_dispatch_success(self) -> None:
        """Test successful dispatch of push notification."""
        item = MockWorkItem(
            identifier="test-123",
            channel="push",
            payload={"title": "Test", "body": "Hello"},
        )
        result = self.processor.process(item)
        self.assertIsInstance(result, RunResult)
        self.assertTrue(result.success)
        self.assertEqual(result.processor, "push")
        self.assertIn("push:test", result.details)
