{"summary": "The OpenRouter retry logic needs to distinguish between fatal authentication errors (e.g., 401/403) and transient errors (e.g., 5xx, rate limits). Currently, the client likely retries all errors. The fix requires updating the OpenRouterClient to inspect HTTP status codes and only retry on transient ones.", "project": {"language": "python"}, "files": [{"path": "elephant_coder/openrouter_client.py", "content": """"OpenRouter API client with intelligent retry logic."""

from __future__ import annotations

import time
from typing import Any

import requests


class OpenRouterResult:
    """Result of an OpenRouter chat completion request."""

    def __init__(self, content: str, model: str):
        self.content = content
        self.model = model


class OpenRouterError(RuntimeError):
    """Error raised when OpenRouter API returns an error."""

    def __init__(self, message: str, status_code: int | None = None):
        super().__init__(message)
        self.status_code = status_code


class OpenRouterClient:
    """Client for OpenRouter API with intelligent retry logic."""

    def __init__(self, api_key: str):
        self.api_key = api_key
        self.base_url = "https://openrouter.ai/api/v1"
        self.max_retries = 3
        self.retry_delay_seconds = 1.0

    def _is_transient_error(self, status_code: int) -> bool:
        """Determine if an HTTP status code indicates a transient error."""
        # Fatal auth errors: 401 Unauthorized, 403 Forbidden
        if status_code in (401, 403):
            return False
        # Transient errors: 429 (rate limit), 5xx server errors
        if status_code == 429 or 500 <= status_code < 600:
            return True
        # Other errors (e.g., 4xx client errors except auth) are non-retryable
        return False

    def chat_completion(self, **kwargs) -> OpenRouterResult:
        """Call OpenRouter chat completions with retry logic."""
        headers = {
            "Authorization": f"Bearer {self.api_key}",
            "Content-Type": "application/json",
        }
        payload = {
            "model": kwargs.get("model", "openai/gpt-4o-mini"),
            "messages": kwargs.get("messages", []),
        }

        last_error: Exception | None = None

        for attempt in range(self.max_retries + 1):
            try:
                response = requests.post(
                    f"{self.base_url}/chat/completions",
                    headers=headers,
                    json=payload,
                    timeout=60,
                )
                response.raise_for_status()
                data = response.json()
                content = data["choices"][0]["message"]["content"]
                model = data.get("model", "unknown")
                return OpenRouterResult(content=content, model=model)
            except requests.HTTPError as e:
                status_code = e.response.status_code if e.response else None
                last_error = e

                # Do not retry fatal auth errors
                if not self._is_transient_error(status_code or 0):
                    raise OpenRouterError(
                        f"Fatal error (status {status_code}): {e}",
                        status_code=status_code,
                    ) from e

                # Retry transient errors
                if attempt < self.max_retries:
                    time.sleep(self.retry_delay_seconds * (2**attempt))  # exponential backoff
                    continue

                raise OpenRouterError(
                    f"Transient error after {self.max_retries} retries (status {status_code}): {e}",
                    status_code=status_code,
                ) from e
            except requests.RequestException as e:
                last_error = e
                if attempt < self.max_retries:
                    time.sleep(self.retry_delay_seconds * (2**attempt))
                    continue
                raise OpenRouterError(f"Request failed after retries: {e}") from e

        raise OpenRouterError(f"Unexpected retry loop exit: {last_error}") from last_error
