def _load_gguf_registry(project_root: Path) -> dict[str, Any]:
    """Load GGUF specialist registry from disk."""
    registry_path = _gguf_registry_path(project_root)
    if not registry_path.exists():
        return _default_gguf_registry()
    try:
        data = json.loads(registry_path.read_text(encoding="utf-8", errors="replace"))
        if not isinstance(data, dict):
            return _default_gguf_registry()
        return data
    except (json.JSONDecodeError, OSError):
        return _default_gguf_registry()


def _specialist_ready(spec: dict[str, Any]) -> tuple[bool, str]:
    """Check if a specialist is ready for use."""
    if not isinstance(spec, dict):
        return False, "spec must be a dict"

    required = ("name", "model", "gguf_path")
    for key in required:
        if key not in spec:
            return False, f"missing required field: {key}"

    if not spec["name"] or not isinstance(spec["name"], str):
        return False, "name must be a non-empty string"

    if not spec["model"] or not isinstance(spec["model"], str):
        return False, "model must be a non-empty string"

    gguf_path = spec.get("gguf_path")
    if gguf_path is not None:
        path = Path(gguf_path)
        if not path.exists():
            return False, f"GGUF file not found: {gguf_path}"

    return True, ""


def _normalized_specialists(project_root: Path) -> list[dict[str, Any]]:
    """Return list of ready specialists."""
    registry = _load_gguf_registry(project_root)
    specialists = registry.get("specialists", {})
    if not isinstance(specialists, dict):
        return []

    ready: list[dict[str, Any]] = []
    for name, spec in specialists.items():
        ok, msg = _specialist_ready(spec)
        if ok:
            spec_copy = dict(spec)
            spec_copy["name"] = name
            ready.append(spec_copy)

    return ready


def _select_local_specialist(
    project_root: Path,
    task: str,
    requested: str | None,
) -> tuple[dict[str, Any] | None, str]:
    """Select a local specialist for the given task."""
    ready = _normalized_specialists(project_root)
    if not ready:
        return None, "no ready specialists available"

    if requested:
        for spec in ready:
            if spec["name"] == requested:
                return spec, ""
        return None, f"requested specialist not found: {requested}"

    # Default selection: first ready specialist
    return ready[0], ""


