def detect_project_root(start_path: str | Path) -> Path:
    """Detect project root by walking up directory tree."""
    path = Path(start_path).resolve()

    # Look for project markers
    markers = ["pyproject.toml", "setup.py", "requirements.txt", ".elephant-coder"]

    for parent in [path] + list(path.parents):
        for marker in markers:
            if (parent / marker).exists():
                return parent

    return path


def ensure_project_layout(project_root: Path) -> Path:
    """Ensure required project directories exist."""
    dirs = [
        project_root / ".elephant-coder" / "personas",
        project_root / ".elephant-coder" / "sessions",
        project_root / ".elephant-coder" / "runs",
        project_root / ".elephant-coder" / "prompts",
    ]

    for d in dirs:
        d.mkdir(parents=True, exist_ok=True)

    # Create active persona marker if missing
    active_marker = project_root / ".elephant-coder" / "active.txt"
    if not active_marker.exists():
        active_marker.write_text("default.md\n", encoding="utf-8")

    return project_root


def load_config(project_root: Path) -> dict[str, Any]:
    """Load configuration from project root."""
    config_map = _default_config_map()

    # Load from pyproject.toml if present
    pyproject = project_root / "pyproject.toml"
    if pyproject.exists():
        try:
            import tomllib
        except ImportError:
            try:
                import tomli as tomllib
            except ImportError:
                tomllib = None

        if tomllib:
            try:
                data = tomllib.loads(pyproject.read_text(encoding="utf-8", errors="replace"))
                tool = data.get("tool", {}).get("elephant", {})
                if isinstance(tool, dict):
                    for key, value in tool.items():
                        config_map[f"{key}"] = value
            except Exception:
                pass

    # Load from .elephant-coder/config.json if present
    config_file = project_root / ".elephant-coder" / "config.json"
    if config_file.exists():
        try:
            data = json.loads(config_file.read_text(encoding="utf-8", errors="replace"))
            if isinstance(data, dict):
                config_map.update(data)
        except Exception:
            pass

    return config_map


