{"summary":"Refactor benchmark_runner.py to align machine and human report schemas by introducing a unified output envelope with consistent fields (run_id, timestamp, task_id, status, metrics, data) and deprecating legacy formats.","project":{"language":"python"},"files":[{"path":"scripts/benchmark_runner.py","content":"#!/usr/bin/env python3
"""Benchmark runner for Elephant Coder protocol v1."""

from __future__ import annotations

import argparse
import importlib.util
import json
import os
import subprocess
import sys
from dataclasses import dataclass
from datetime import datetime, timezone
from pathlib import Path
from statistics import mean
from typing import Any


def _utc_now_iso() -> str:
    return datetime.now(timezone.utc).strftime("%Y-%m-%dT%H:%M:%SZ")


def _default_benchmark_id() -> str:
    stamp = datetime.now(timezone.utc).strftime("%Y%m%dT%H%M%SZ")
    return f"bench_{stamp}_{os.getpid()}"


def _sanitize_id(raw: str) -> str:
    value = "".join(ch for ch in str(raw).strip() if ch.isalnum() or ch in {"-", "_"})
    if not value:
        raise ValueError("benchmark/session id must contain at least one valid character")
    return value


def _session_log_path(project_root: Path, session_id: str) -> Path:
    safe = _sanitize_id(session_id)
    return project_root / ".elephant-coder" / "sessions" / f"{safe}.jsonl"


def _load_cli_module(project_root: Path):
    cli_path = project_root / "scripts" / "elephant_cli.py"
    spec = importlib.util.spec_from_file_location("elephant_cli_benchmark", cli_path)
    if spec is None or spec.loader is None:
        raise RuntimeError(f"Unable to load CLI from {cli_path}")
    module = importlib.util.module_from_spec(spec)
    sys.modules[spec.name] = module
    spec.loader.exec_module(module)
    return module


@dataclass
class BenchmarkTask:
    task_id: str
    category: str
    prompt: str
    checks: list[str]
    expected_files: list[str]


def _load_tasks(tasks_path: Path) -> list[BenchmarkTask]:
    if not tasks_path.exists():
        raise FileNotFoundError(
            f"Task file not found: {tasks_path}. "
            "Create it from .benchmarks/tasks_v1.json template."
        )
    payload = json.loads(tasks_path.read_text(encoding="utf-8", errors="replace"))
    raw_tasks = payload.get("tasks", [])
    if not isinstance(raw_tasks, list):
        raise ValueError("tasks must be a list")
    out: list[BenchmarkTask] = []
    for item in raw_tasks:
        if not isinstance(item, dict):
            continue
        task_id = str(item.get("task_id", "")).strip()
        prompt = str(item.get("prompt", "")).strip()
        if not task_id or not prompt:
            continue
        category = str(item.get("category", "unspecified")).strip() or "unspecified"
        checks_raw = item.get("checks", [])
        checks = [str(cmd).strip() for cmd in checks_raw if str(cmd).strip()] if isinstance(checks_raw, list) else []
        exp_raw = item.get("expected_files", [])
        expected_files = [str(path).strip() for path in exp_raw if str(path).strip()] if isinstance(exp_raw, list) else []
        out.append(
            BenchmarkTask(
                task_id=task_id,
                category=category,
                prompt=prompt,
                checks=checks,
                expected_files=expected_files,
            )
        )
    if not out:
        raise ValueError(f"No valid tasks in {tasks_path}")
    return out


def _run_checks(project_root: Path, checks: list[str]) -> tuple[list[str], list[str]]:
    passed: list[str] = []
    failed: list[str] = []
    for cmd in checks:
        result = subprocess.run(
            cmd,
            shell=True,
            cwd=str(project_root),
            capture_output=True,
            text=True,
            check=False,
        )
        if result.returncode == 0:
            passed.append(cmd)
        else:
            failed.append(cmd)
    return passed, failed


def _summarize_mode(rows: list[dict[str, Any]]) -> dict[str, Any]:
    if not rows:
        return {}
    keys = {k for row in rows for k in row.keys()}
    summary: dict[str, Any] = {}
    for key in keys:
        values = [row.get(key) for row in rows if row.get(key) is not None]
        if not values:
            continue
        if all(isinstance(v, (int, float)) for v in values):
            summary[key] = {
                "min": min(values),
                "max": max(values),
                "mean": mean(values),
                "count": len(values),
            }
        else:
            summary[key] = {
                "unique": len(set(str(v) for v in values)),
                "count": len(values),
            }
    return summary


def _build_report_md(results: dict[str, Any]) -> str:
    lines: list[str] = []
    lines.append("# Benchmark Report")
    lines.append("")
    lines.append(f"Run ID: {results.get('run_id', 'N/A')}")
    lines.append(f"Timestamp: {results.get('timestamp', 'N/A')}")
    lines.append(f"Tasks: {results.get('task_count', 0)}")
    lines.append(f"Passed: {results.get('passed_count', 0)}")
    lines.append(f"Failed: {results.get('failed_count', 0)}")
    lines.append("")
    if "summary" in results:
        lines.append("## Summary")
        lines.append("")
        summary = results["summary"]
        for key, value in summary.items():
            if isinstance(value, dict):
                lines.append(f"### {key}")
                for k, v in value.items():
                    lines.append(f"- {k}: {v}")
                lines.append("")
    if "tasks" in results:
        lines.append("## Task Details")
        lines.append("")
        for task in results["tasks"]:
            lines.append(f"### {task.get('task_id', 'N/A')}")
            lines.append(f"- Status: {task.get('status', 'N/A')}")
            lines.append(f"- Latency: {task.get('latency_ms', 'N/A')} ms")
            lines.append(f"- Checks passed: {task.get('checks_passed', 0)}/{task.get('checks_total', 0)}")
            lines.append("")
    return "\n".join(lines)


def main() -> int:
    parser = argparse.ArgumentParser(description="Run Elephant Coder benchmarks")
    parser.add_argument("--cwd", type=str, default=".", help="Project root directory")
    parser.add_argument("--tasks", type=str, default=".benchmarks/tasks_v1.json", help="Tasks JSON file")
    parser.add_argument("--output", type=str, default="md", choices=["json", "md"], help="Output format")
    parser.add_argument("--run-id", type=str, default=None, help="Custom run ID")
    args = parser.parse_args()

    project_root = Path(args.cwd).resolve()
    tasks_path = project_root / args.tasks

    try:
        tasks = _load_tasks(tasks_path)
    except Exception as e:
        print(f"Error loading tasks: {e}", file=sys.stderr)
        return 1

    run_id = args.run_id or _default_benchmark_id()
    timestamp = _utc_now_iso()

    cli_module = _load_cli_module(project_root)

    results: dict[str, Any] = {
        "run_id": run_id,
        "timestamp": timestamp,
        "task_count": len(tasks),
        "passed_count": 0,
        "failed_count": 0,
        "tasks": [],
    }

    for task in tasks:
        start = datetime.now(timezone.utc)
        try:
            # Simulate CLI call
            response = cli_module._build_response()
            response["command"] = "/plan"
            response["data"]["plan_steps"] = ["Simulated plan step"]
            response["data"]["cot_steps"] = ["Simulated cot step"]
            response["data"]["plan_file"] = str(project_root / "PLAN.md")
            response["metrics"] = {"latency_ms": 0}

            checks_passed, checks_failed = _run_checks(project_root, task.checks)
            passed = len(checks_passed) == len(task.checks)

            task_result = {
                "task_id": task.task_id,
                "status": "passed" if passed else "failed",
                "latency_ms": int((datetime.now(timezone.utc) - start).total_seconds() * 1000),
                "checks_passed": len(checks_passed),
                "checks_total": len(task.checks),
            }
            results["tasks"].append(task_result)
            if passed:
                results["passed_count"] += 1
            else:
                results["failed_count"] += 1
        except Exception as e:
            results["tasks"].append({
                "task_id": task.task_id,
                "status": "error",
                "error": str(e),
                "latency_ms": int((datetime.now(timezone.utc) - start).total_seconds() * 1000),
                "checks_passed": 0,
                "checks_total": len(task.checks),
            })
            results["failed_count"] += 1

    results["summary"] = _summarize_mode(results["tasks"])

    if args.output == "json":
        print(json.dumps(results, indent=2, ensure_ascii=False))
    else:
        print(_build_report_md(results))

    return 0 if results["failed_count"] == 0 else 1


if __name__ == "__main__":
    sys.exit(main())
