from __future__ import annotations

import time
from typing import Any


class OpenRouterResult:
    """Result of an OpenRouter API call."""

    def __init__(self, content: str, model: str):
        self.content = content
        self.model = model


class OpenRouterError(RuntimeError):
    """Base error for OpenRouter API issues."""

    def __init__(self, message: str, status_code: int | None = None):
        super().__init__(message)
        self.status_code = status_code


class OpenRouterClient:
    """Client for OpenRouter API with intelligent retry logic."""

    def __init__(self, api_key: str):
        self.api_key = api_key
        self._max_retries = 3
        self._retry_delay = 1.0

    def _is_transient_error(self, error: OpenRouterError) -> bool:
        """Determine if an error is transient (retryable) or fatal."""
        if error.status_code is None:
            return True  # Network errors are transient
        # Fatal auth errors: 401 Unauthorized, 403 Forbidden
        if error.status_code in (401, 403):
            return False
        # Transient errors: 429 Too Many Requests, 5xx Server Errors
        if error.status_code in (429, 500, 502, 503, 504):
            return True
        # Other errors are considered fatal by default
        return False

    def chat_completion(self) -> OpenRouterResult:
        """Call OpenRouter chat completion with retry logic."""
        import urllib.request
        import urllib.error
        import json

        url = "https://openrouter.ai/api/v1/chat/completions"
        headers = {
            "Authorization": f"Bearer {self.api_key}",
            "Content-Type": "application/json",
        }
        payload = json.dumps({"model": "openai/gpt-4", "messages": [{"role": "user", "content": "Hello"}]}).encode()

        retries = 0
        while True:
            try:
                req = urllib.request.Request(url, data=payload, headers=headers, method="POST")
                with urllib.request.urlopen(req, timeout=30) as response:
                    result = json.loads(response.read().decode())
                    content = result["choices"][0]["message"]["content"]
                    model = result["model"]
                    return OpenRouterResult(content=content, model=model)
            except urllib.error.HTTPError as e:
                error = OpenRouterError(str(e), status_code=e.code)
                if not self._is_transient_error(error) or retries >= self._max_retries:
                    raise error
                retries += 1
                time.sleep(self._retry_delay * (2 ** (retries - 1)))  # Exponential backoff
            except Exception as e:
                error = OpenRouterError(str(e))
                if not self._is_transient_error(error) or retries >= self._max_retries:
                    raise error
                retries += 1
                time.sleep(self._retry_delay * (2 ** (retries - 1)))
