@dataclass(frozen=True)
class RuntimeConfig:
    """Runtime configuration after defaults, file config, and CLI overrides."""

    output: str
    default_model: str
    model_fallbacks: tuple[str, ...]
    model_max_retries: int
    model_retry_backoff_sec: float
    max_input_tokens: int
    max_output_tokens: int
    max_cost_usd: float


class CLIError(RuntimeError):
    """Known CLI-level exception with stable error code."""

    def __init__(self, code: str, message: str):
        if code not in ERROR_CODES:
            raise ValueError(f"Unknown CLI error code: {code}")
        super().__init__(message)
        self.code = code
        self.message = message


def _utc_now_iso() -> str:
    """Return UTC timestamp in RFC3339-like format."""
    return datetime.now(timezone.utc).strftime("%Y-%m-%dT%H:%M:%SZ")


def _make_id(prefix: str) -> str:
    """Build a short deterministic-looking run/session ID."""
    return f"{prefix}_{uuid.uuid4().hex[:12]}"


def _normalize_argv(argv: list[str]) -> list[str]:
    """Map slash commands to argparse command names and handle shorthand args."""
    if not argv:
        return argv

    normalized = []
    i = 0
    while i < len(argv):
        arg = argv[i]

        # Handle slash-prefixed commands
        if arg.startswith("/") and len(arg) > 1:
            normalized.append(arg[1:])
            i += 1
            continue

        # Handle shorthand arguments like -t for --task
        if arg.startswith("-") and len(arg) > 1:
            if arg == "-t":
                normalized.append("--task")
            elif arg == "-c":
                normalized.append("--cwd")
            elif arg == "-o":
                normalized.append("--output")
            elif arg == "-m":
                normalized.append("--model")
            elif arg == "-b":
                normalized.append("--budget")
            else:
                normalized.append(arg)
            i += 1
            continue

        normalized.append(arg)
        i += 1

    return normalized


def _parse_scalar(value: str) -> Any:
    """Parse scalar config values from text."""
    raw = value.strip()
    low = raw.lower()
    if low in {"true", "false"}:
        return low == "true"
    try:
        if "." in raw:
            return float(raw)
        return int(raw)
    except ValueError:
        pass
    if "," in raw:
        return [part.strip() for part in raw.split(",") if part.strip()]
    return raw


def _default_config_map() -> dict[str, Any]:
    """Return flat default config values."""
    return {
        "output.default": "text",
        "model.default": "gpt-4o-mini",
        "model.fallbacks": [],
        "model.max_retries": 2,
        "model.retry_backoff_sec": 1.0,
        "budgets.max_input_tokens": 12000,
        "budgets.max_output_tokens": 3000,
        "budgets.max_cost_usd": 1.0,
        "plugins.allowed_permissions": [],
    }


def _gguf_registry_path(project_root: Path) -> Path:
    """Return path to local GGUF specialist registry."""
    return project_root / ".elephant-coder" / "gguf_specialists.json"


def _default_gguf_registry() -> dict[str, Any]:
    """Return default GGUF specialist registry scaffold."""
    return {
        "version": 1,
        "specialists": {},
    }


def _load_gguf_registry(project_root: Path) -> dict[str, Any]:
    """Load GGUF specialist registry from disk."""
    registry_path = _gguf_registry_path(project_root)
    if not registry_path.exists():
        return _default_gguf_registry()
    try:
        payload = json.loads(registry_path.read_text(encoding="utf-8", errors="replace"))
        if not isinstance(payload, dict):
            return _default_gguf_registry()
        return payload
    except Exception:
        return _default_gguf_registry()


def _specialist_ready(spec: dict[str, Any]) -> tuple[bool, str]:
    """Check if a specialist is ready for use."""
    if not spec.get("enabled", False):
        return False, "disabled"
    if not spec.get("gguf_path"):
        return False, "missing GGUF path"
    if not spec.get("model_name"):
        return False, "missing model name"
    return True, ""


def _normalized_specialists(project_root: Path) -> list[dict[str, Any]]:
    """Return list of ready specialists."""
    registry = _load_gguf_registry(project_root)
    specialists = registry.get("specialists", {})
    ready = []
    for name, spec in specialists.items():
        ok, reason = _specialist_ready(spec)
        if ok:
            item = {"name": name, **spec}
            ready.append(item)
    return ready


def _select_local_specialist(project_root: Path, task: str, requested: str | None) -> tuple[dict[str, Any] | None, str]:
    """Select a local specialist for the given task."""
    ready = _normalized_specialists(project_root)
    if not ready:
        return None, "no specialists available"

    if requested:
        for spec in ready:
            if spec["name"] == requested:
                return spec, ""
        return None, f"requested specialist '{requested}' not found or not ready"

    # Default selection: first ready specialist
    return ready[0], ""


def _route_for_code() -> tuple[dict[str, Any], list[str]]:
    """Determine code generation route."""
    return {"mode": "default"}, []


def _messages_to_local_prompt(messages: list[dict[str, str]]) -> str:
    """Convert messages to local specialist prompt."""
    parts = []
    for msg in messages:
        role = msg.get("role", "user")
        content = msg.get("content", "")
        parts.append(f"{role.upper()}: {content}")
    return "\n\n".join(parts)


def _run_local_specialist_inference() -> tuple[str, dict[str, int], float]:
    """Run local specialist inference."""
    return "placeholder", {"prompt_tokens": 0, "completion_tokens": 0, "total_tokens": 0}, 0.0


def _default_elephant_root() -> Path:
    """Return default Elephant root directory."""
    return Path.home() / ".elephant-coder"


def detect_project_root(start_path: str | Path) -> Path:
    """Detect project root by walking up directory tree."""
    path = Path(start_path).resolve()
    for parent in [path] + list(path.parents):
        if (parent / "pyproject.toml").exists():
            return parent
        if (parent / ".elephant-coder").exists():
            return parent
    return path


def ensure_project_layout(project_root: Path) -> Path:
    """Ensure required project directories exist."""
    dirs = [
        project_root / ".elephant-coder" / "sessions",
        project_root / ".elephant-coder" / "runs",
        project_root / ".elephant-coder" / "personas",
        project_root / ".elephant-coder" / "prompts",
    ]
    for d in dirs:
        d.mkdir(parents=True, exist_ok=True)
    return project_root


def load_config(project_root: Path) -> dict[str, Any]:
    """Load config from pyproject.toml or elephant.toml."""
    config = _default_config_map()

    pyproject = project_root / "pyproject.toml"
    if pyproject.exists():
        try:
            import tomllib
        except ImportError:
            try:
                import tomli as tomllib
            except ImportError:
                tomllib = None

        if tomllib:
            try:
                data = tomllib.loads(pyproject.read_text(encoding="utf-8", errors="replace"))
                tool = data.get("tool", {})
                elephant = tool.get("elephant", {})
                if isinstance(elephant, dict):
                    for key, value in elephant.items():
                        config[f"{key}"] = value
            except Exception:
                pass

    return config


def resolve_runtime_config(args: argparse.Namespace, config: dict[str, Any]) -> RuntimeConfig:
    """Resolve runtime config from args and config."""
    output = getattr(args, "output", None) or config.get("output.default", "text")
    default_model = getattr(args, "model", None) or config.get("model.default", "gpt-4o-mini")
    model_fallbacks = tuple(config.get("model.fallbacks", []))
    model_max_retries = int(config.get("model.max_retries", 2))
    model_retry_backoff_sec = float(config.get("model.retry_backoff_sec", 1.0))
    max_input_tokens = int(config.get("budgets.max_input_tokens", 12000))
    max_output_tokens = int(config.get("budgets.max_output_tokens", 3000))
    max_cost_usd = float(config.get("budgets.max_cost_usd", 1.0))

    return RuntimeConfig(
        output=str(output),
        default_model=str(default_model),
        model_fallbacks=model_fallbacks,
        model_max_retries=model_max_retries,
        model_retry_backoff_sec=model_retry_backoff_sec,
        max_input_tokens=max_input_tokens,
        max_output_tokens=max_output_tokens,
        max_cost_usd=max_cost_usd,
    )


def _build_response() -> dict[str, Any]:
    """Build standard response envelope."""
    return {
        "ok": True,
        "command": None,
        "data": {},
        "metrics": {"latency_ms": 0},
        "run_id": None,
    }


def _write_run_record(base: Path, response: dict[str, Any]) -> None:
    """Write run record to disk."""
    run_id = response.get("run_id")
    if not run_id:
        return
    runs_dir = base / "runs"
    runs_dir.mkdir(parents=True, exist_ok=True)
    run_file = runs_dir / f"{run_id}.json"
    run_file.write_text(json.dumps(response, indent=2, ensure_ascii=False), encoding="utf-8")


