def _gguf_registry_path(project_root: Path) -> Path:
    """Return path to local GGUF specialist registry."""
    return project_root / ".elephant-coder" / "gguf_specialists.json"


def _default_gguf_registry() -> dict[str, Any]:
    """Return default GGUF specialist registry scaffold."""
    return {
        "version": 1,
        "specialists": [],
    }


def _load_gguf_registry(project_root: Path) -> dict[str, Any]:
    """Load GGUF specialist registry from disk."""
    registry_path = _gguf_registry_path(project_root)
    if not registry_path.exists():
        return _default_gguf_registry()
    try:
        data = json.loads(registry_path.read_text(encoding="utf-8", errors="replace"))
        if not isinstance(data, dict):
            return _default_gguf_registry()
        return data
    except (json.JSONDecodeError, OSError):
        return _default_gguf_registry()


def _specialist_ready(spec: dict[str, Any]) -> tuple[bool, str]:
    """Check if a specialist is ready for inference."""
    if not isinstance(spec, dict):
        return False, "specialist entry must be a dictionary"
    path = spec.get("gguf_path")
    if not path:
        return False, "gguf_path is missing"
    p = Path(path)
    if not p.is_file():
        return False, f"gguf file not found: {p}"
    return True, ""


def _normalized_specialists(project_root: Path) -> list[dict[str, Any]]:
    """Return validated specialists list."""
    registry = _load_gguf_registry(project_root)
    raw = registry.get("specialists", [])
    if not isinstance(raw, list):
        return []
    out: list[dict[str, Any]] = []
    for item in raw:
        if not isinstance(item, dict):
            continue
        name = str(item.get("name", "")).strip()
        if not name:
            continue
        ready, reason = _specialist_ready(item)
        out.append({
            "name": name,
            "ready": ready,
            "reason": reason,
            "gguf_path": str(item.get("gguf_path", "")),
        })
    return out


def _select_local_specialist(project_root: Path, task: str, requested: str | None) -> tuple[dict[str, Any] | None, str]:
    """Select a local specialist for the given task."""
    specialists = _normalized_specialists(project_root)
    if not specialists:
        return None, "no local specialists registered"
    if requested:
        for spec in specialists:
            if spec["name"] == requested:
                if spec["ready"]:
                    return spec, ""
                return None, spec["reason"]
        return None, f"specialist not found: {requested}"
    # Default to first ready specialist
    for spec in specialists:
        if spec["ready"]:
            return spec, ""
    return None, "no ready specialists available"


