def _parse_scalar(value: str) -> Any:
    """Parse scalar config values from text."""
    raw = value.strip()
    low = raw.lower()
    if low in {"true", "false"}:
        return low == "true"
    try:
        if "." in raw:
            return float(raw)
        return int(raw)
    except ValueError:
        pass
    if "," in raw:
