def load_config(project_root: Path) -> dict[str, Any]:
    """Load config from pyproject.toml or defaults."""
    config: dict[str, Any] = _default_config_map().copy()
    pyproject = project_root / "pyproject.toml"
    if pyproject.exists():
        try:
            import tomllib
        except ImportError:
            try:
                import tomli as tomllib
            except ImportError:
                return config
        data = tomllib.loads(pyproject.read_text(encoding="utf-8", errors="replace"))
        tool = data.get("tool", {})
        elephant = tool.get("elephant", {})
        if isinstance(elephant, dict):
            for key, value in elephant.items():
                if isinstance(value, dict):
                    for k, v in value.items():
                        config[f"{key}.{k}"] = v
                else:
                    config[key] = value
    return config


