"""Elephant Coder CLI scaffold (Phase 1).

This module provides the command router, response envelope, project-local
layout bootstrap, config loading, and placeholder command handlers.
"""

from __future__ import annotations

import argparse
import hashlib
import json
import os
import shutil
import shlex
import subprocess
import sys
import tempfile
import time
import uuid
from dataclasses import dataclass
from datetime import datetime, timezone
from pathlib import Path
from typing import Any

_SCRIPT_ROOT = Path(__file__).resolve().parents[1]

COMMANDS = ("plan", "code", "agents", "persona", "plugins", "git", "stats", "test", "session")
ERROR_CODES = {
    "E_CONFIG",
    "E_BUDGET",
    "E_MODEL",
    "E_INDEX",
    "E_IMPACT",
    "E_PLUGIN_PERMISSION",
    "E_STORAGE",
    "E_TEST",
}


def _utc_now_iso() -> str:
    return datetime.now(timezone.utc).strftime("%Y-%m-%dT%H:%M:%SZ")


def _default_session_id() -> str:
    stamp = datetime.now(timezone.utc).strftime("%Y%m%dT%H%M%SZ")
    return f"sess_{stamp}_{os.getpid()}"


def _sanitize_id(raw: str) -> str:
    value = "".join(ch for ch in str(raw).strip() if ch.isalnum() or ch in {"-", "_"})
    if not value:
        raise ValueError("session id must contain at least one valid character")
    return value


def _session_log_path(project_root: Path, session_id: str) -> Path:
    safe = _sanitize_id(session_id)
    return project_root / ".elephant-coder" / "sessions" / f"{safe}.jsonl"


def _load_cli_module(project_root: Path):
    cli_path = project_root / ".elephant-coder" / "cli.py"
    if not cli_path.exists():
        return None
    spec = importlib.util.spec_from_file_location("elephant_cli_local", cli_path)
    if not spec or not spec.loader:
        return None
    mod = importlib.util.module_from_spec(spec)
    spec.loader.exec_module(mod)
    return mod


def _ensure_project_structure(project_root: Path) -> None:
    dirs = [
        project_root / ".elephant-coder",
        project_root / ".elephant-coder" / "sessions",
        project_root / ".elephant-coder" / "plugins",
    ]
    for d in dirs:
        d.mkdir(parents=True, exist_ok=True)


def _load_config(project_root: Path) -> dict[str, Any]:
    cfg_path = project_root / ".elephant-coder" / "config.json"
    if not cfg_path.exists():
        return {}
    try:
        with open(cfg_path, "r", encoding="utf-8") as f:
            return json.load(f)
    except Exception:
        return {}


def _ensure_config(project_root: Path) -> dict[str, Any]:
    cfg = _load_config(project_root)
    if "session_id" not in cfg:
        cfg["session_id"] = _default_session_id()
        _save_config(project_root, cfg)
    return cfg


def _save_config(project_root: Path, cfg: dict[str, Any]) -> None:
    cfg_path = project_root / ".elephant-coder" / "config.json"
    cfg_path.parent.mkdir(parents=True, exist_ok=True)
    with open(cfg_path, "w", encoding="utf-8") as f:
        json.dump(cfg, f, indent=2)


def _log_session(project_root: Path, session_id: str, entry: dict[str, Any]) -> None:
    log_path = _session_log_path(project_root, session_id)
    log_path.parent.mkdir(parents=True, exist_ok=True)
    with open(log_path, "a", encoding="utf-8") as f:
        f.write(json.dumps(entry) + "\n")


def _run_command(
    project_root: Path,
    cmd: str,
    args: list[str],
    session_id: str,
    user_input: str,
) -> tuple[str, str | None]:
    """Execute a command handler and return (output, error)."""
    local_cli = _load_cli_module(project_root)
    if local_cli and hasattr(local_cli, "handle_command"):
        try:
            result = local_cli.handle_command(project_root, cmd, args, user_input)
            return (str(result), None)
        except Exception as e:
            return ("", f"local handler error: {e}")

    # Fallback handlers
    if cmd == "plan":
        return _handle_plan(project_root, args, user_input)
    elif cmd == "code":
        return _handle_code(project_root, args, user_input)
    elif cmd == "agents":
        return _handle_agents(project_root, args, user_input)
    elif cmd == "persona":
        return _handle_persona(project_root, args, user_input)
    elif cmd == "plugins":
        return _handle_plugins(project_root, args, user_input)
    elif cmd == "git":
        return _handle_git(project_root, args, user_input)
    elif cmd == "stats":
        return _handle_stats(project_root, args, user_input)
    elif cmd == "test":
        return _handle_test(project_root, args, user_input)
    elif cmd == "session":
        return _handle_session(project_root, args, user_input)
    else:
        return (f"Unknown command: {cmd}", None)


def _handle_plan(project_root: Path, args: list[str], user_input: str) -> tuple[str, str | None]:
    """Generate a plan and persist it to PLAN.md."""
    from elephant_coder.index_engine import IndexService
    from elephant_coder.state_store import StateStore
    from elephant_coder.openrouter_client import OpenRouterClient

    try:
        index_service = IndexService(project_root / ".elephant-coder" / "index.db")
        state_store = StateStore(project_root / ".elephant-coder" / "index.db")
        client = OpenRouterClient(os.getenv("OPENROUTER_API_KEY", ""))

        # Simulate plan generation
        plan_content = f"# Plan\n\nGenerated at {_utc_now_iso()}\n\nUser request: {user_input}\n\nSteps:\n1. Analyze project structure\n2. Identify key files and dependencies\n3. Propose implementation steps\n"

        # Persist plan to PLAN.md
        plan_path = project_root / "PLAN.md"
        with open(plan_path, "w", encoding="utf-8") as f:
            f.write(plan_content)

        output = f"Plan generated and saved to {plan_path}\n\n{plan_content}"
        return (output, None)
    except Exception as e:
        return ("", f"plan error: {e}")


def _handle_code(project_root: Path, args: list[str], user_input: str) -> tuple[str, str | None]:
    return ("Code generation not implemented yet.", None)


def _handle_agents(project_root: Path, args: list[str], user_input: str) -> tuple[str, str | None]:
    return ("Agent configuration not implemented yet.", None)


def _handle_persona(project_root: Path, args: list[str], user_input: str) -> tuple[str, str | None]:
    return ("Persona configuration not implemented yet.", None)


def _handle_plugins(project_root: Path, args: list[str], user_input: str) -> tuple[str, str | None]:
    return ("Plugin management not implemented yet.", None)


def _handle_git(project_root: Path, args: list[str], user_input: str) -> tuple[str, str | None]:
    return ("Git integration not implemented yet.", None)


def _handle_stats(project_root: Path, args: list[str], user_input: str) -> tuple[str, str | None]:
    from elephant_coder.index_engine import IndexService

    try:
        index_service = IndexService(project_root / ".elephant-coder" / "index.db")
        stats = index_service.index_stats()
        output = f"Index stats:\n{json.dumps(stats, indent=2)}"
        return (output, None)
    except Exception as e:
        return ("", f"stats error: {e}")


def _handle_test(project_root: Path, args: list[str], user_input: str) -> tuple[str, str | None]:
    return ("Test execution not implemented yet.", None)


def _handle_session(project_root: Path, args: list[str], user_input: str) -> tuple[str, str | None]:
    cfg = _load_config(project_root)
    output = f"Current session ID: {cfg.get('session_id', 'none')}"
    return (output, None)


def main() -> int:
    parser = argparse.ArgumentParser(description="Elephant Coder CLI")
    parser.add_argument("command", choices=COMMANDS, help="Command to run")
    parser.add_argument("args", nargs="*", help="Command arguments")
    parser.add_argument("--project", type=str, default=".", help="Project root directory")
    parser.add_argument("--input", type=str, default="", help="User input for the command")
    args = parser.parse_args()

    project_root = Path(args.project).resolve()
    _ensure_project_structure(project_root)
    config = _ensure_config(project_root)
    session_id = config["session_id"]

    output, error = _run_command(project_root, args.command, args.args, session_id, args.input)

    if error:
        print(f"Error: {error}", file=sys.stderr)
        return 1

    print(output)

    # Log session
    _log_session(
        project_root,
        session_id,
        {
            "timestamp": _utc_now_iso(),
            "command": args.command,
            "args": args.args,
            "input": args.input,
            "output": output,
        },
    )

    return 0


if __name__ == "__main__":
    sys.exit(main())
