def _default_elephant_root() -> Path:
    """Return default Elephant project root."""
    return Path.home() / ".elephant"


def detect_project_root(start_path: str | Path) -> Path:
    """Detect Elephant project root from start_path."""
    path = Path(start_path).resolve()
    while path != path.parent:
        if (path / ".elephant-coder").exists():
            return path
        path = path.parent
    return detect_project_root(path.parent)


def ensure_project_layout(project_root: Path) -> Path:
    """Ensure Elephant project layout exists."""
    dirs = [
        project_root / ".elephant-coder" / "runs",
        project_root / ".elephant-coder" / "sessions",
        project_root / ".elephant-coder" / "personas",
        project_root / ".elephant-coder" / "benchmarks",
    ]
    for d in dirs:
        d.mkdir(parents=True, exist_ok=True)
    return project_root


def load_config(project_root: Path) -> dict[str, Any]:
    """Load config from project root."""
    config_path = project_root / ".elephant-coder" / "config.json"
    if not config_path.exists():
        return _default_config_map()
    try:
        payload = json.loads(config_path.read_text(encoding="utf-8", errors="replace"))
        if not isinstance(payload, dict):
            return _default_config_map()
        return payload
    except Exception:
        return _default_config_map()


def resolve_runtime_config(args: argparse.Namespace, config: dict[str, Any]) -> RuntimeConfig:
    """Resolve runtime config from args and config."""
    output = getattr(args, "output", "text")
    if not isinstance(output, str):
        output = "text"
    default_model = config.get("model.default", "gpt-4o-mini")
    if not isinstance(default_model, str):
        default_model = "gpt-4o-mini"
    fallbacks_raw = config.get("model.fallbacks", [])
    if not isinstance(fallbacks_raw, list):
        fallbacks_raw = []
    fallbacks = tuple(str(f) for f in fallbacks_raw if isinstance(f, str) and f.strip())
    max_retries = config.get("model.max_retries", 2)
    if not isinstance(max_retries, int) or max_retries < 0:
        max_retries = 2
    retry_backoff = config.get("model.retry_backoff_sec", 1.0)
    if not isinstance(retry_backoff, (int, float)) or retry_backoff < 0:
        retry_backoff = 1.0
    max_input = config.get("budgets.max_input_tokens", 12000)
    if not isinstance(max_input, int) or max_input <= 0:
        max_input = 12000
    max_output = config.get("budgets.max_output_tokens", 3000)
    if not isinstance(max_output, int) or max_output <= 0:
        max_output = 3000
    max_cost = config.get("budgets.max_cost_usd", 1.0)
    if not isinstance(max_cost, (int, float)) or max_cost < 0:
        max_cost = 1.0
    return RuntimeConfig(
        output=output,
        default_model=default_model,
        model_fallbacks=fallbacks,
        model_max_retries=max_retries,
        model_retry_backoff_sec=float(retry_backoff),
        max_input_tokens=max_input,
        max_output_tokens=max_output,
        max_cost_usd=float(max_cost),
    )


