"""Benchmark runner for Elephant Coder protocol v1."""

from __future__ import annotations

import argparse
import importlib.util
import json
import os
import subprocess
import sys
from dataclasses import dataclass
from datetime import datetime, timezone
from pathlib import Path
from statistics import mean
from typing import Any


def _utc_now_iso() -> str:
    return datetime.now(timezone.utc).strftime("%Y-%m-%dT%H:%M:%SZ")


def _default_benchmark_id() -> str:
    stamp = datetime.now(timezone.utc).strftime("%Y%m%dT%H%M%SZ")
    return f"bench_{stamp}_{os.getpid()}"


def _sanitize_id(raw: str) -> str:
    value = "".join(ch for ch in str(raw).strip() if ch.isalnum() or ch in {"-", "_"})
    if not value:
        raise ValueError("benchmark/session id must contain at least one valid character")
    return value


def _session_log_path(project_root: Path, session_id: str) -> Path:
    safe = _sanitize_id(session_id)
    return project_root / ".elephant-coder" / "sessions" / f"{safe}.jsonl"


def _load_cli_module(project_root: Path):
    cli_path = project_root / "scripts" / "elephant_cli.py"
    spec = importlib.util.spec_from_file_location("elephant_cli", cli_path)
    if spec is None or spec.loader is None:
        raise ImportError(f"Cannot load CLI from {cli_path}")
    module = importlib.util.module_from_spec(spec)
    spec.loader.exec_module(module)
    return module


def _run_benchmark_command(
    project_root: Path,
    cli_module: Any,
    command: str,
    extra_args: list[str],
) -> dict[str, Any]:
    """Run a benchmark command and capture metrics."""
    start = datetime.now(timezone.utc)
    try:
        result = subprocess.run(
            [sys.executable, str(project_root / "scripts" / "elephant_cli.py"), command] + extra_args,
            capture_output=True,
            text=True,
            timeout=600,
            check=True,
        )
        ok = True
        stderr = result.stderr
    except subprocess.CalledProcessError as exc:
        ok = False
        stderr = exc.stderr or ""
    except subprocess.TimeoutExpired:
        ok = False
        stderr = "Command timed out"
    elapsed_ms = int((datetime.now(timezone.utc) - start).total_seconds() * 1000)
    return {
        "ok": ok,
        "latency_ms": elapsed_ms,
        "stderr": stderr,
    }


def _parse_run_metrics(log_entries: list[dict[str, Any]]) -> dict[str, Any]:
    """Parse run metrics from log entries."""
    runs = []
    for entry in log_entries:
        if entry.get("type") == "run":
            runs.append(entry)
    if not runs:
        return {}
    tokens = [r.get("tokens", 0) for r in runs]
    costs = [r.get("cost_usd", 0.0) for r in runs]
    latencies = [r.get("latency_ms", 0) for r in runs]
    ok_count = sum(1 for r in runs if r.get("ok", False))
    return {
        "count": len(runs),
        "ok_count": ok_count,
        "tokens_total": sum(tokens),
        "tokens_mean": round(mean(tokens), 2) if tokens else 0,
        "cost_usd_total": round(sum(costs), 4),
        "cost_usd_mean": round(mean(costs), 6) if costs else 0.0,
        "latency_ms_total": sum(latencies),
        "latency_ms_mean": round(mean(latencies), 2) if latencies else 0,
    }


def _generate_report(
    project_root: Path,
    session_id: str,
    baseline_metrics: dict[str, Any] | None,
) -> dict[str, Any]:
    """Generate benchmark report with aggregate token reduction and quality deltas."""
    session_log = _session_log_path(project_root, session_id)
    if not session_log.exists():
        return {"error": f"Session log not found: {session_log}"}
    log_entries = []
    with session_log.open("r", encoding="utf-8") as fh:
        for line in fh:
            line = line.strip()
            if line:
                try:
                    log_entries.append(json.loads(line))
                except json.JSONDecodeError:
                    continue
    metrics = _parse_run_metrics(log_entries)
    if not metrics:
        return {"error": "No run metrics found in session log"}
    report = {
        "session_id": session_id,
        "generated_at": _utc_now_iso(),
        "metrics": metrics,
    }
    if baseline_metrics:
        if metrics.get("tokens_total") and baseline_metrics.get("tokens_total"):
            delta_tokens = metrics["tokens_total"] - baseline_metrics["tokens_total"]
            pct_reduction = round((delta_tokens / baseline_metrics["tokens_total"]) * -100, 2) if baseline_metrics["tokens_total"] else 0.0
            report["quality_deltas"] = {
                "tokens_reduction_pct": pct_reduction,
                "tokens_delta": delta_tokens,
            }
        if metrics.get("ok_count") and baseline_metrics.get("ok_count"):
            ok_delta = metrics["ok_count"] - baseline_metrics["ok_count"]
            report["quality_deltas"]["ok_count_delta"] = ok_delta
    return report


def main():
    parser = argparse.ArgumentParser(description="Elephant Coder benchmark runner")
    parser.add_argument("--id", default=_default_benchmark_id(), help="Session ID")
    parser.add_argument("--baseline", help="Path to baseline metrics JSON for delta calculation")
    parser.add_argument("--report", action="store_true", help="Generate report after runs")
    parser.add_argument("--run", nargs="+", default=[], help="Commands to run")
    args = parser.parse_args()
    project_root = Path(__file__).resolve().parent.parent
    project_root.mkdir(exist_ok=True)
    cli_module = _load_cli_module(project_root)
    if args.report:
        baseline = None
        if args.baseline:
            try:
                with open(args.baseline, "r", encoding="utf-8") as fh:
                    baseline = json.load(fh)
            except Exception as exc:
                print(f"Warning: Could not load baseline: {exc}", file=sys.stderr)
        report = _generate_report(project_root, args.id, baseline)
        print(json.dumps(report, indent=2))
        return
    if not args.run:
        parser.print_help()
        return
    session_log = _session_log_path(project_root, args.id)
    session_log.parent.mkdir(parents=True, exist_ok=True)
    for cmd in args.run:
        if cmd == "index":
            result = _run_benchmark_command(project_root, cli_module, "index", [])
        elif cmd == "impact":
            result = _run_benchmark_command(project_root, cli_module, "impact", ["--max-depth", "2"])
        elif cmd == "chat":
            result = _run_benchmark_command(project_root, cli_module, "chat", ["What is the main purpose of this project?"])
        else:
            print(f"Unknown command: {cmd}", file=sys.stderr)
            continue
        entry = {
            "type": "run",
            "command": cmd,
            "run_id": f"run_{os.urandom(4).hex()}",
            "timestamp": _utc_now_iso(),
            **result,
        }
        with session_log.open("a", encoding="utf-8") as fh:
            fh.write(json.dumps(entry) + "\n")


if __name__ == "__main__":
    main()
