def detect_project_root(start_path: str | Path) -> Path:
    """Detect Elephant project root from start path."""
    path = Path(start_path).resolve()
    while path != path.parent:
        if (path / ".elephant-coder").exists():
            return path
        path = path.parent
    return detect_project_root(path.parent)


def ensure_project_layout(project_root: Path) -> Path:
    """Ensure Elephant project layout exists."""
    layout = {
        ".elephant-coder": {
            "config.json": json.dumps({"version": 1}, indent=2),
            "personas": {},
            "sessions": {},
            "runs": {},
            "gguf_specialists.json": json.dumps(_default_gguf_registry()),
        },
    }

    def _ensure_tree(root: Path, tree: dict[str, Any]) -> None:
        for name, content in tree.items():
            child = root / name
            if isinstance(content, dict):
                child.mkdir(parents=True, exist_ok=True)
                _ensure_tree(child, content)
            else:
                if not child.exists():
                    child.write_text(content, encoding="utf-8")

    _ensure_tree(project_root, layout)
    return project_root


def load_config(project_root: Path) -> dict[str, Any]:
    """Load config from project root."""
    config_path = project_root / ".elephant-coder" / "config.json"
    if not config_path.exists():
        return _default_config_map()
    try:
        payload = json.loads(config_path.read_text(encoding="utf-8", errors="replace"))
        if not isinstance(payload, dict):
            return _default_config_map()
        return payload
    except Exception:
        return _default_config_map()


def resolve_runtime_config(args: argparse.Namespace, config: dict[str, Any]) -> RuntimeConfig:
    """Resolve runtime config from args and config."""
    output = getattr(args, "output", None) or config.get("output.default", "text")
    default_model = config.get("model.default", "gpt-4o-mini")
    model_fallbacks = tuple(config.get("model.fallbacks", []))
    model_max_retries = int(config.get("model.max_retries", 2))
    model_retry_backoff_sec = float(config.get("model.retry_backoff_sec", 1.0))
    max_input_tokens = int(config.get("budgets.max_input_tokens", 12000))
    max_output_tokens = int(config.get("budgets.max_output_tokens", 3000))
    max_cost_usd = float(config.get("budgets.max_cost_usd", 1.0))
    session_mode = getattr(args, "session_mode", "interactive")

    return RuntimeConfig(
        output=str(output),
        default_model=str(default_model),
        model_fallbacks=model_fallbacks,
        model_max_retries=model_max_retries,
        model_retry_backoff_sec=model_retry_backoff_sec,
        max_input_tokens=max_input_tokens,
        max_output_tokens=max_output_tokens,
        max_cost_usd=max_cost_usd,
        session_mode=session_mode,
    )


