{"summary": "Refactor stats metrics calculation into focused helpers with stable return shapes.", "project": {"language": "python"}, "files": [{"path": "elephant_coder/index_engine.py", "content": "from __future__ import annotations

import ast
import hashlib
from collections import defaultdict
from pathlib import Path
from typing import Any

from .state_store import StateStore


def _sha256_text(value: str) -> str:
    return hashlib.sha256(value.encode("utf-8")).hexdigest()


def _normalize_rel(path: Path, project_root: Path) -> str:
    try:
        return str(path.relative_to(project_root))
    except ValueError:
        return str(path)


def _safe_unparse(node: ast.AST | None) -> str:
    if node is None:
        return ""
    try:
        return ast.unparse(node)
    except Exception:
        return ""


def _extract_imports(tree: ast.Module) -> list[str]:
    imports: list[str] = []
    for node in ast.walk(tree):
        if isinstance(node, ast.Import):
            for alias in node.names:
                imports.append(alias.name)
        elif isinstance(node, ast.ImportFrom):
            module = node.module or ""
            for alias in node.names:
                name = alias.name
                if alias.asname:
                    name = f"{name} as {alias.asname}"
                imports.append(f"{module}.{name}" if node.level else name)
    return imports


class _Collector(ast.NodeVisitor):
    def __init__(self) -> None:
        self.functions: list[dict[str, Any]] = []
        self.classes: list[dict[str, Any]] = []
        self.calls: list[dict[str, str]] = []
        self._qualname_stack: list[str] = []

    def _qualname(self, name: str) -> str:
        if self._qualname_stack:
            return ".".join([*self._qualname_stack, name])
        return name

    def _record_function(
        self, node: ast.FunctionDef | ast.AsyncFunctionDef, kind: str
    ) -> None:
        self.functions.append(
            {
                "name": node.name,
                "qualname": self._qualname(node.name),
                "kind": kind,
                "signature": _function_signature(node, kind),
                "line": node.lineno,
                "end_line": getattr(node, "end_lineno", node.lineno),
            }
        )

    def visit_ClassDef(self, node: ast.ClassDef) -> None:
        self._qualname_stack.append(node.name)
        self.generic_visit(node)
        self._qualname_stack.pop()

    def visit_FunctionDef(self, node: ast.FunctionDef) -> None:
        self._record_function(node, "sync")
        self._qualname_stack.append(node.name)
        self.generic_visit(node)
        self._qualname_stack.pop()

    def visit_AsyncFunctionDef(self, node: ast.AsyncFunctionDef) -> None:
        self._record_function(node, "async")
        self._qualname_stack.append(node.name)
        self.generic_visit(node)
        self._qualname_stack.pop()

    def visit_Call(self, node: ast.Call) -> None:
        func_name = _safe_unparse(node.func)
        self.calls.append({"target": func_name, "line": node.lineno})
        self.generic_visit(node)


def _function_signature(
    node: ast.FunctionDef | ast.AsyncFunctionDef, kind: str
) -> str:
    args: list[str] = []
    for arg in node.args.args:
        parts = [arg.arg]
        if arg.annotation:
            parts.append(": " + _safe_unparse(arg.annotation))
        args.append("".join(parts))
    if node.args.vararg:
        args.append("*" + node.args.vararg.arg)
    if node.args.kwarg:
        args.append("**" + node.args.kwarg.arg)
    return f"({', '.join(args)})"


def _parse_python_file(path: Path) -> tuple[list[dict[str, Any]], list[str], list[dict[str, str]], str | None]:
    try:
        source = path.read_text(encoding="utf-8")
        tree = ast.parse(source, filename=str(path))
    except SyntaxError as e:
        return [], [], [], str(e)
    except Exception as e:
        return [], [], [], str(e)

    collector = _Collector()
    collector.visit(tree)
    imports = _extract_imports(tree)

    return collector.functions, imports, collector.calls, None


class IndexService:
    def __init__(self, project_root: Path) -> None:
        self._project_root = project_root.resolve()
        self._state = StateStore(self._project_root / ".elephant-coder" / "index.db")
        self._edges: dict[str, set[str]] = defaultdict(set)

    def close(self) -> None:
        self._state.close()

    def _iter_python_files(self) -> list[Path]:
        return list(self._project_root.rglob("*.py"))

    def _resolve_import_to_file(self, module_name: str) -> str | None:
        parts = module_name.split(".")
        for suffix in ["", "__init__.py"]:
            candidate = self._project_root
            for part in parts:
                candidate /= part
            if suffix:
                candidate = candidate.with_name(f"{candidate.name}.{suffix}")
            if candidate.is_file():
                return _normalize_rel(candidate, self._project_root)
        return None

    def _rebuild_edges(self) -> int:
        self._edges.clear()
        imports = self._state.list_imports()
        for imp in imports:
            target = self._resolve_import_to_file(imp["module"])
            if target:
                self._edges[imp["source"]].add(target)
        return sum(len(v) for v in self._edges.values())

    def refresh_index(self) -> dict[str, Any]:
        files = self._iter_python_files()
        for path in files:
            rel = _normalize_rel(path, self._project_root)
            mtime = path.stat().st_mtime
            file_hash = _sha256_text(path.read_text(encoding="utf-8"))
            symbols, imports, calls, error = _parse_python_file(path)
            self._state.upsert_indexed_file(rel, file_hash, mtime, 0.0, error)
            self._state.replace_file_analysis(rel, symbols, imports, calls)
        edge_count = self._rebuild_edges()
        return {"files_indexed": len(files), "edges_built": edge_count}

    def _build_cognitive_world(self) -> tuple[Any | None, dict[str, Any]]:
        symbols = self._state.list_symbols()
        calls = self._state.list_calls()
        files_meta = self._state.get_indexed_file_meta()
        stats = self.index_stats()
        world = {
            "files": files_meta,
            "symbols": symbols,
            "calls": calls,
            "edges": dict(self._edges),
            "stats": stats,
        }
        return None, world

    def impact_for_files(self, changed_files: list[str], max_depth: int) -> dict[str, Any]:
        affected: set[str] = set(changed_files)
        queue = list(changed_files)
        depth = 0
        while queue and depth < max_depth:
            next_queue: list[str] = []
            for src in queue:
                for dst in self._edges.get(src, set()):
                    if dst not in affected:
                        affected.add(dst)
                        next_queue.append(dst)
            queue = next_queue
            depth += 1
        return {
            "changed": changed_files,
            "affected": list(affected),
            "max_depth": max_depth,
        }

    def index_stats(self) -> dict[str, int]:
        symbols = self._state.list_symbols()
        calls = self._state.list_calls()
        imports = self._state.list_imports()
        files_meta = self._state.get_indexed_file_meta()
        return {
            "total_files": len(files_meta),
            "total_functions": sum(1 for s in symbols if s["kind"] in ("sync", "async")),
            "total_classes": sum(1 for s in symbols if s["kind"] == "class"),
            "total_calls": len(calls),
            "total_imports": len(imports),
        }
