    """Parse a slash-command with shorthand arguments.

    Handles edge cases like:
    - Shorthand flags (e.g., /cmd -f)
    - Combined flags (e.g., /cmd -abc)
    - Flag-value pairs (e.g., /cmd -o output.txt)
    - Mixed positional and optional args

    Returns a dict with 'command', 'flags', and 'args'.
    """
    import shlex

    parts = shlex.split(command.strip())
    if not parts or not parts[0].startswith('/'):
        return {'command': '', 'flags': {}, 'args': []}

    cmd_name = parts[0][1:]  # strip leading '/'
    flags = {}
    args = []
    i = 1

    while i < len(parts):
        part = parts[i]
        if part.startswith('-'):
            flag = part.lstrip('-')
            # Handle combined short flags (e.g., -abc)
            if len(flag) > 1 and not flag[0].isdigit():
                for ch in flag:
                    flags[ch] = True
            else:
                # Single flag; check for value
                if i + 1 < len(parts) and not parts[i + 1].startswith('-'):
                    flags[flag] = parts[i + 1]
                    i += 1
                else:
                    flags[flag] = True
        else:
            args.append(part)
        i += 1

    return {'command': cmd_name, 'flags': flags, 'args': args}
