def _append_session_event(project_root: Path, session_id: str, role: str, content: str) -> None:
    """Append a session event to the session log."""
    log_path = _session_log_path(project_root, session_id)
    log_path.parent.mkdir(parents=True, exist_ok=True)
    event = {
        "timestamp": _utc_now_iso(),
        "role": role,
        "content": content,
    }
    try:
        with log_path.open("a", encoding="utf-8") as f:
            f.write(json.dumps(event, ensure_ascii=False) + "\n")
    except Exception:
        pass


def _load_session_context(project_root: Path, session_id: str) -> tuple[str, dict[str, Any]]:
    """Load session context from log."""
    log_path = _session_log_path(project_root, session_id)
    if not log_path.exists():
        return "", {}
    try:
        lines = log_path.read_text(encoding="utf-8", errors="replace").strip().splitlines()
    except Exception:
        return "", {}
    if not lines:
        return "", {}
    try:
        last = json.loads(lines[-1])
    except Exception:
        last = {"role": "unknown", "content": ""}
    context = ""
    for line in lines:
        try:
            ev = json.loads(line)
            if ev.get("role") == "user":
                context += ev.get("content", "") + "\n"
        except Exception:
            continue
    return context.strip(), last


