from __future__ import annotations

import sqlite3
from pathlib import Path
from typing import Any


class StateStore:
    """SQLite-backed state store for Elephant Coder."""

    def __init__(self, project_root: Path):
        self._db_path = project_root / ".elephant-coder" / "state.db"
        self._db_path.parent.mkdir(parents=True, exist_ok=True)
        self._conn = sqlite3.connect(str(self._db_path))
        self._create_tables()

    def _create_tables(self):
        with self._conn:
            self._conn.execute(
                """
                CREATE TABLE IF NOT EXISTS file_index (
                    path TEXT PRIMARY KEY,
                    sha256 TEXT NOT NULL,
                    modified_at REAL NOT NULL,
                    symbols TEXT NOT NULL,
                    imports TEXT NOT NULL,
                    docstring TEXT
                )
                """
            )
            self._conn.execute(
                """
                CREATE TABLE IF NOT EXISTS prompt_cache (
                    prompt_hash TEXT PRIMARY KEY,
                    response TEXT NOT NULL,
                    created_at REAL NOT NULL
                )
                """
            )

    def close(self):
        if self._conn:
            self._conn.close()

    def upsert_file_index(
        self,
        path: str,
        sha256: str,
        modified_at: float,
        symbols: str,
        imports: str,
        docstring: str | None,
    ):
        with self._conn:
            self._conn.execute(
                """
                INSERT OR REPLACE INTO file_index
                (path, sha256, modified_at, symbols, imports, docstring)
                VALUES (?, ?, ?, ?, ?, ?)
                """,
                (path, sha256, modified_at, symbols, imports, docstring or ""),
            )

    def get_file_index(self, path: str) -> dict[str, Any] | None:
        cur = self._conn.execute(
            "SELECT sha256, modified_at, symbols, imports, docstring FROM file_index WHERE path = ?",
            (path,),
        )
        row = cur.fetchone()
        if row:
            return {
                "sha256": row[0],
                "modified_at": row[1],
                "symbols": row[2],
                "imports": row[3],
                "docstring": row[4] if row[4] else None,
            }
        return None

    def get_prompt_cache(self, prompt_hash: str) -> str | None:
        cur = self._conn.execute(
            "SELECT response FROM prompt_cache WHERE prompt_hash = ?",
            (prompt_hash,),
        )
        row = cur.fetchone()
        return row[0] if row else None

    def upsert_prompt_cache(self, prompt_hash: str, response: str):
        with self._conn:
            self._conn.execute(
                """
                INSERT OR REPLACE INTO prompt_cache (prompt_hash, response, created_at)
                VALUES (?, ?, ?)
                """,
                (prompt_hash, response, time.time()),
            )

    def delete_file(self, path: str):
        with self._conn:
            self._conn.execute("DELETE FROM file_index WHERE path = ?", (path,))

    def list_files(self) -> list[str]:
        cur = self._conn.execute("SELECT path FROM file_index")
        return [row[0] for row in cur.fetchall()]

    def clear(self):
        with self._conn:
            self._conn.execute("DELETE FROM file_index")
            self._conn.execute("DELETE FROM prompt_cache")
