from __future__ import annotations

import ast
import hashlib
import time
from collections import deque
from pathlib import Path
from typing import Any

from .state_store import StateStore

_SKIP_DIRS = {
    ".elephant-coder",
    ".git",
    ".venv",
    "venv",
    "__pycache__",
    ".mypy_cache",
    ".pytest_cache",
    "node_modules",
}


def _sha256_text(value: str) -> str:
    return hashlib.sha256(value.encode("utf-8")).hexdigest()


def _normalize_rel(path: Path, project_root: Path) -> str:
    return str(path.relative_to(project_root))


def _safe_unparse(node: ast.AST | None) -> str:
    return ast.unparse(node) if node else ""


def _extract_imports(tree: ast.Module) -> list[str]:
    imports = []
    for node in ast.walk(tree):
        if isinstance(node, ast.Import):
            for alias in node.names:
                imports.append(alias.name)
        elif isinstance(node, ast.ImportFrom):
            if node.module:
                imports.append(node.module)
    return imports


class _Collector(ast.NodeVisitor):
    def __init__(self):
        self.functions: list[dict[str, Any]] = []
        self.classes: list[dict[str, Any]] = []
        self.imports: list[str] = []
        self.docstring: str | None = None

    def _qualname(self, name: str) -> str:
        return name

    def _record_function(
        self, node: ast.FunctionDef | ast.AsyncFunctionDef, kind: str
    ) -> None:
        self.functions.append(
            {
                "name": self._qualname(node.name),
                "kind": kind,
                "signature": _function_signature(node, kind),
            }
        )

    def visit_ClassDef(self, node: ast.ClassDef) -> None:
        self.classes.append(
            {
                "name": self._qualname(node.name),
                "docstring": ast.get_docstring(node),
            }
        )
        self.generic_visit(node)

    def visit_FunctionDef(self, node: ast.FunctionDef) -> None:
        self._record_function(node, "function")
        self.generic_visit(node)

    def visit_AsyncFunctionDef(self, node: ast.AsyncFunctionDef) -> None:
        self._record_function(node, "async_function")
        self.generic_visit(node)

    def visit_Call(self, node: ast.Call) -> None:
        if isinstance(node.func, ast.Name):
            self.imports.append(node.func.id)
        self.generic_visit(node)


def _function_signature(
    node: ast.FunctionDef | ast.AsyncFunctionDef, kind: str
) -> str:
    args = []
    for arg in node.args.args:
        annotation = _safe_unparse(arg.annotation)
        default = _safe_unparse(arg.default) if arg.default else None
        if annotation:
            if default:
                args.append(f"{arg.arg}: {annotation} = {default}")
            else:
                args.append(f"{arg.arg}: {annotation}")
        else:
            args.append(arg.arg)
    return f"def {node.name}({', '.join(args)})"


def _parse_python_file(path: Path) -> tuple[list[dict[str, Any]], list[str], list[dict[str, str]], str | None]:
    with open(path, "r", encoding="utf-8") as f:
        source = f.read()
    tree = ast.parse(source)
    collector = _Collector()
    collector.visit(tree)
    imports = _extract_imports(tree)
    docstring = ast.get_docstring(tree)
    return collector.functions, collector.classes, [{"name": imp} for imp in imports], docstring


class IndexService:
    def __init__(self, project_root: Path):
        self._project_root = project_root
        self._state = StateStore(project_root)

    def close(self):
        self._state.close()

    def _iter_python_files(self) -> list[Path]:
        return [
            p
            for p in self._project_root.rglob("*.py")
            if not any(d in p.parts for d in _SKIP_DIRS)
        ]

    def _resolve_import_to_file(self, module_name: str) -> str | None:
        parts = module_name.split(".")
        for part in parts:
            candidate = self._project_root / part
            if candidate.is_file() and candidate.suffix == ".py":
                return str(candidate.relative_to(self._project_root))
            if candidate.is_dir() and (candidate / "__init__.py").is_file():
                return str(candidate.relative_to(self._project_root))
        return None

    def index_all(self):
        for path in self._iter_python_files():
            try:
                functions, classes, imports, docstring = _parse_python_file(path)
                sha256 = _sha256_text(path.read_text(encoding="utf-8"))
                symbols = str(functions + classes)
                import_names = [imp["name"] for imp in imports]
                rel_path = _normalize_rel(path, self._project_root)
                self._state.upsert_file_index(
                    rel_path,
                    sha256,
                    time.time(),
                    symbols,
                    str(import_names),
                    docstring,
                )
            except Exception:
                pass

    def get_impact(self, path: str) -> list[str]:
        rel_path = Path(path)
        if not rel_path.is_absolute():
            rel_path = self._project_root / path
        rel_str = _normalize_rel(rel_path, self._project_root)
        affected = []
        for other in self._state.list_files():
            if other == rel_str:
                continue
            try:
                other_path = self._project_root / other
                with open(other_path, "r", encoding="utf-8") as f:
                    source = f.read()
                if rel_str in source:
                    affected.append(other)
            except Exception:
                pass
        return affected
