      "path": "scripts/benchmark_runner.py",
      "content": "import json\nimport os\nfrom pathlib import Path\nfrom statistics import mean\n\ndef generate_benchmark_report(results: list[dict[str, Any]], output_path: Path) -> None:\n    total_tokens = sum(result['tokens'] for result in results)\n    avg_quality = mean(result['quality'] for result in results)\n    report = {\n        'total_tokens': total_tokens,\n        'average_quality': avg_quality,\n        'results': results\n    }\n    with open(output_path, 'w') as f:\n        json.dump(report, f, indent=4)\n"
