from statistics import mean

class BenchmarkReport:
    def __init__(self, runs):
        self.runs = runs

    def aggregate(self):
        total_tokens = sum(run['tokens'] for run in self.runs)
        avg_tokens = mean(run['tokens'] for run in self.runs)
        quality_deltas = [run['quality_delta'] for run in self.runs]
        return {
            'total_tokens': total_tokens,
            'avg_tokens': avg_tokens,
            'quality_deltas': quality_deltas
        }

