{"summary": "Refactored command dispatch internals to minimize duplication in warnings and runtime metadata, enhancing maintainability and clarity.", "project": {"language": "Python"}, "files": [{"path": "scripts/elephant_cli.py", "content": "#!/usr/bin/env python3
"""Elephant Coder CLI scaffold (Phase 1).

This module provides the command router, response envelope, project-local
layout bootstrap, config loading, and placeholder command handlers.
"""

from __future__ import annotations

import argparse
import hashlib
import json
import os
import shlex
import subprocess
import sys
import time
import uuid
from dataclasses import dataclass
from datetime import datetime, timezone
from pathlib import Path
from typing import Any

_SCRIPT_ROOT = Path(__file__).resolve().parents[1]

COMMANDS = ("plan", "code", "agents", "persona", "plugins", "git", "stats", "test", "session")
ERROR_CODES = {
    "E_CONFIG",
    "E_BUDGET",
    "E_MODEL",
    "E_INDEX",
    "E_IMPACT",
    "E_PLUGIN_PERMISSION",
    "E_STORAGE",
    "E_TEST",
}


@dataclass(frozen=True)
class RuntimeConfig:
    """Immutable runtime configuration."""
    project_root: Path
    config: dict[str, Any]


def _load_config(project_root: Path) -> dict[str, Any]:
    """Load configuration from .elephant.json or fallback defaults."""
    config_path = project_root / ".elephant.json"
    if config_path.exists():
        try:
            return json.loads(config_path.read_text(encoding="utf-8"))
        except json.JSONDecodeError as exc:
            raise RuntimeError(f"Invalid JSON in {config_path}") from exc
    return {}


def _build_runtime_config() -> RuntimeConfig:
    """Build runtime config from environment and filesystem."""
    project_root = _SCRIPT_ROOT
    config = _load_config(project_root)
    return RuntimeConfig(project_root=project_root, config=config)


def _emit_response(
    ok: bool,
    command: str,
    payload: dict[str, Any],
    warnings: list[str] | None = None,
    runtime_metadata: dict[str, Any] | None = None,
) -> None:
    """Emit standardized JSON response envelope."""
    envelope = {
        "ok": ok,
        "command": command,
        "payload": payload,
        "warnings": warnings or [],
        "runtime_metadata": runtime_metadata or {},
    }
    print(json.dumps(envelope, indent=2))


def _warn_missing_command(cmd: str) -> list[str]:
    """Generate warning for unknown command."""
    return [f"Unknown command '{cmd}'. Available: {', '.join(COMMANDS)}"]


def _warn_missing_project_root() -> list[str]:
    """Generate warning for missing project root."""
    return ["Project root not found; using script directory."]


def _build_runtime_metadata(start_time: float) -> dict[str, Any]:
    """Build standardized runtime metadata."""
    return {
        "started_at": datetime.fromtimestamp(start_time, tz=timezone.utc).isoformat(),
        "duration_ms": round((time.time() - start_time) * 1000, 2),
    }


def _handle_plan(config: RuntimeConfig) -> dict[str, Any]:
    """Plan command handler."""
    return {"status": "planning", "project": str(config.project_root)}


def _handle_code(config: RuntimeConfig) -> dict[str, Any]:
    """Code command handler."""
    return {"status": "coding", "project": str(config.project_root)}


def _handle_agents(config: RuntimeConfig) -> dict[str, Any]:
    """Agents command handler."""
    return {"status": "agents", "project": str(config.project_root)}


def _handle_persona(config: RuntimeConfig) -> dict[str, Any]:
    """Persona command handler."""
    return {"status": "persona", "project": str(config.project_root)}


def _handle_plugins(config: RuntimeConfig) -> dict[str, Any]:
    """Plugins command handler."""
    return {"status": "plugins", "project": str(config.project_root)}


def _handle_git(config: RuntimeConfig) -> dict[str, Any]:
    """Git command handler."""
    return {"status": "git", "project": str(config.project_root)}


def _handle_stats(config: RuntimeConfig) -> dict[str, Any]:
    """Stats command handler."""
    return {"status": "stats", "project": str(config.project_root)}


def _handle_test(config: RuntimeConfig) -> dict[str, Any]:
    """Test command handler."""
    return {"status": "test", "project": str(config.project_root)}


def _handle_session(config: RuntimeConfig) -> dict[str, Any]:
    """Session command handler."""
    return {"status": "session", "project": str(config.project_root)}


def _dispatch_command(cmd: str, config: RuntimeConfig) -> tuple[bool, dict[str, Any], list[str]]:
    """Dispatch command to handler, returning success, payload, and warnings."""
    handlers = {
        "plan": _handle_plan,
        "code": _handle_code,
        "agents": _handle_agents,
        "persona": _handle_persona,
        "plugins": _handle_plugins,
        "git": _handle_git,
        "stats": _handle_stats,
        "test": _handle_test,
        "session": _handle_session,
    }
    handler = handlers.get(cmd)
    if handler is None:
        return False, {}, _warn_missing_command(cmd)
    try:
        payload = handler(config)
        return True, payload, []
    except Exception as exc:
        return False, {"error": str(exc)}, [f"Command '{cmd}' failed: {exc}"]


def main() -> int:
    """CLI entry point."""
    start_time = time.time()
    parser = argparse.ArgumentParser(description="Elephant Coder CLI")
    parser.add_argument("command", nargs="?", default=None, help="Command to run")
    args = parser.parse_args()

    try:
        config = _build_runtime_config()
    except RuntimeError as exc:
        _emit_response(False, "init", {"error": str(exc)}, warnings=_warn_missing_project_root())
        return 1

    cmd = args.command
    if cmd is None or cmd not in COMMANDS:
        _emit_response(False, cmd or "unknown", {}, warnings=_warn_missing_command(cmd or ""))
        return 1

    ok, payload, warnings = _dispatch_command(cmd, config)
    runtime_metadata = _build_runtime_metadata(start_time)
    _emit_response(ok, cmd, payload, warnings=warnings, runtime_metadata=runtime_metadata)
    return 0 if ok else 1


if __name__ == "__main__":
    sys.exit(main())
