from __future__ import annotations

from pathlib import Path


def load_dotenv(project_root: Path, filename: str = ".env") -> int:
    """Load KEY=VALUE pairs from a dotenv file into process env.

    Existing environment variables are not overwritten.

    Returns:
        Number of keys loaded.
    """
    import os

    dotenv_path = project_root / filename
    if not dotenv_path.exists():
        return 0

    loaded = 0
    with dotenv_path.open() as f:
        for line in f:
            line = line.strip()
            if not line or line.startswith("#"):
                continue
            if "=" not in line:
                continue
            key, _, value = line.partition("=")
            key = key.strip()
            value = value.strip()
            if key and value and key not in os.environ:
                os.environ[key] = value
                loaded += 1
    return loaded


def sanitize_identifier(name: str) -> str:
    """Sanitize a string for safe use as an identifier or label."""
    return "".join(c if c.isalnum() or c in "_-" else "_" for c in name).strip("_")


def classify_file_type(path: Path) -> str:
    """Classify a file by its extension."""
    ext = path.suffix.lower()
    if ext == ".py":
        return "python"
    elif ext == ".md":
        return "markdown"
    elif ext in (".json", ".yaml", ".yml"):
        return "config"
    elif ext in (".txt", ".log"):
        return "text"
    return "other"


def normalize_module_name(module: str) -> str:
    """Normalize a Python module name to canonical form."""
    return module.replace(".", "/").strip("/")


def is_hidden_path(path: Path) -> bool:
    """Check if any component of the path is hidden (starts with '.')."""
    return any(part.startswith(".") for part in path.parts)


def extract_base_name(path: Path) -> str:
    """Extract the base name without extension."""
    return path.stem


def ensure_relative_path(path: Path, root: Path) -> Path:
    """Ensure path is relative to root."""
    try:
        return path.relative_to(root)
    except ValueError:
        return path


def parse_key_value_line(line: str) -> tuple[str, str] | None:
    """Parse a KEY=VALUE line; returns (key, value) or None if invalid."""
    line = line.strip()
    if not line or line.startswith("#"):
        return None
    if "=" not in line:
        return None
    key, _, value = line.partition("=")
    key = key.strip()
    value = value.strip()
    if not key or not value:
        return None
    return key, value
