from __future__ import annotations

import ast
import hashlib
import time
from collections import deque
from pathlib import Path
from typing import Any

from .state_store import StateStore

_SKIP_DIRS = {
    ".elephant-coder",
    ".git",
    ".venv",
    "venv",
    "__pycache__",
    ".mypy_cache",
    ".pytest_cache",
    ".ruff_cache",
    "build",
    "dist",
}


def _sha256_text(value: str) -> str:
    """Return SHA256 hex digest of a string."""
    return hashlib.sha256(value.encode("utf-8")).hexdigest()


def _normalize_rel(path: Path, project_root: Path) -> str:
    """Normalize a path relative to project root."""
    try:
        rel = path.relative_to(project_root)
    except ValueError:
        rel = path
    return str(rel).replace("\\", "/")


def _safe_unparse(node: ast.AST | None) -> str:
    """Safely unparse an AST node to source."""
    if node is None:
        return ""
    try:
        return ast.unparse(node)  # type: ignore[attr-defined]
    except Exception:
        return ""


def _extract_imports(tree: ast.Module) -> list[str]:
    """Extract all import module names from an AST."""
    imports: list[str] = []
    for node in ast.walk(tree):
        if isinstance(node, ast.Import):
            for alias in node.names:
                imports.append(alias.name)
        elif isinstance(node, ast.ImportFrom):
            if node.module:
                imports.append(node.module)
    return imports


class _Collector(ast.NodeVisitor):
    def __init__(self) -> None:
        self.functions: list[dict[str, Any]] = []
        self.classes: list[dict[str, Any]] = []
        self.calls: list[str] = []
        self._qualname_stack: list[str] = []

    def _qualname(self, name: str) -> str:
        if self._qualname_stack:
            return ".".join(self._qualname_stack) + "." + name
        return name

    def _record_function(
        self, node: ast.FunctionDef | ast.AsyncFunctionDef, kind: str
    ) -> None:
        self.functions.append(
            {
                "name": node.name,
                "qualname": self._qualname(node.name),
                "kind": kind,
                "signature": _function_signature(node, kind),
            }
        )

    def _function_signature(
        node: ast.FunctionDef | ast.AsyncFunctionDef, kind: str
    ) -> str:
        args = []
        for arg in node.args.args:
            parts = [arg.arg]
            if arg.annotation:
                parts.append(": " + _safe_unparse(arg.annotation))
            args.append("".join(parts))
        if node.args.vararg:
            args.append("*" + node.args.vararg.arg)
        if node.args.kwarg:
            args.append("**" + node.args.kwarg.arg)
        return f"({', '.join(args)})"

    def visit_ClassDef(self, node: ast.ClassDef) -> None:
        self._qualname_stack.append(node.name)
        self.classes.append(
            {
                "name": node.name,
                "qualname": self._qualname(node.name),
                "bases": [_safe_unparse(b) for b in node.bases],
            }
        )
        self.generic_visit(node)
        self._qualname_stack.pop()

    def visit_FunctionDef(self, node: ast.FunctionDef) -> None:
        self._record_function(node, "sync")
        self.generic_visit(node)

    def visit_AsyncFunctionDef(self, node: ast.AsyncFunctionDef) -> None:
        self._record_function(node, "async")
        self.generic_visit(node)

    def visit_Call(self, node: ast.Call) -> None:
        if isinstance(node.func, ast.Name):
            self.calls.append(node.func.id)
        elif isinstance(node.func, ast.Attribute):
            self.calls.append(_safe_unparse(node.func))
        self.generic_visit(node)


def _parse_python_file(path: Path) -> tuple[list[dict[str, Any]], list[str], list[dict[str, str]], str | None]:
    """Parse a Python file and extract functions, imports, classes, and docstring."""
    try:
        source = path.read_text(encoding="utf-8")
    except Exception:
        return [], [], [], None

    try:
        tree = ast.parse(source, filename=str(path))
    except SyntaxError:
        return [], [], [], None

    collector = _Collector()
    collector.visit(tree)

    imports = _extract_imports(tree)
    docstring = ast.get_docstring(tree)

    return collector.functions, imports, collector.classes, docstring


class IndexService:
    def __init__(self, project_root: Path) -> None:
        self.project_root = project_root.resolve()
        self._store = StateStore(self.project_root / ".elephant-coder" / "state.json")

    def close(self) -> None:
        self._store.close()

    def _iter_python_files(self) -> list[Path]:
        """Return list of Python files under project root, excluding hidden dirs."""
        files: list[Path] = []
        for path in self.project_root.rglob("*.py"):
            if any(part.startswith(".") for part in path.relative_to(self.project_root).parts):
                continue
            files.append(path)
        return files

    def _resolve_import_to_file(self, module_name: str) -> str | None:
        """Resolve a module name to a file path."""
        parts = module_name.split(".")
        candidate = self.project_root / "/".join(parts)
        if candidate.with_suffix(".py").exists():
            return str(candidate.with_suffix(".py"))
        if candidate.is_dir() and (candidate / "__init__.py").exists():
            return str(candidate / "__init__.py")
        return None
