"""Elephant Coder CLI scaffold (Phase 1).

This module provides the command router, response envelope, project-local
layout bootstrap, config loading, and placeholder command handlers.
"""

from __future__ import annotations

import argparse
import hashlib
import json
import os
import shlex
import subprocess
import sys
import time
import uuid
from dataclasses import dataclass
from datetime import datetime, timezone
from pathlib import Path
from typing import Any

_SCRIPT_ROOT = Path(__file__).resolve().parents[1]

COMMANDS = ("plan", "code", "agents", "persona", "plugins", "git", "stats", "test", "session")
ERROR_CODES = {
    "E_CONFIG",
    "E_BUDGET",
    "E_MODEL",
    "E_INDEX",
    "E_IMPACT",
    "E_PLUGIN_PERMISSION",
    "E_STORAGE",
    "E_TEST",
}


@dataclass(frozen=True)
class RuntimeConfig:
    """Runtime configuration after defaults, file config, and CLI overrides."""

    output: str
    default_model: str
    model_fallbacks: tuple[str, ...]
    model_max_retries: int
    model_retry_backoff_sec: float
    max_input_tokens: int
    max_output_tokens: int
    max_cost_usd: float


class CLIError(RuntimeError):
    """Known CLI-level exception with stable error code."""

    def __init__(self, code: str, message: str):
        if code not in ERROR_CODES:
            raise ValueError(f"Unknown CLI error code: {code}")
        super().__init__(message)
        self.code = code
        self.message = message


def _utc_now_iso() -> str:
    """Return UTC timestamp in RFC3339-like format."""
    return datetime.now(timezone.utc).strftime("%Y-%m-%dT%H:%M:%SZ")


def _make_id(prefix: str) -> str:
    """Build a short deterministic-looking run/session ID."""
    return f"{prefix}_{uuid.uuid4().hex[:12]}"


def _normalize_argv(argv: list[str]) -> list[str]:
    """Map slash commands to argparse command names."""
    if not argv:
        return argv
    if argv[0].startswith("/") and len(argv[0]) > 1:
        argv = [argv[0][1:]] + argv[1:]
    return argv


def _parse_scalar(value: str) -> Any:
    """Parse scalar config values from text."""
    raw = value.strip()
    low = raw.lower()
    if low in {"true", "false"}:
        return low == "true"
    try:
        if "." in raw:
            return float(raw)
        return int(raw)
    except ValueError:
        pass
    if "," in raw:
        return [part.strip() for part in raw.split(",") if part.strip()]
    return raw


def _default_config_map() -> dict[str, Any]:
    """Return flat default config values."""
    return {
        "output.default": "text",
        "model.default": "gpt-4o-mini",
        "model.fallbacks": [],
        "model.max_retries": 2,
        "model.retry_backoff_sec": 1.0,
        "budgets.max_input_tokens": 12000,
        "budgets.max_output_tokens": 3000,
        "budgets.max_cost_usd": 1.0,
        "plugins.allowed_permissions": [],
    }


def _default_elephant_root() -> Path:
    """Return the Elephant Coder project root (directory above scripts/)."""
    return Path(__file__).resolve().parents[1]


def detect_project_root(start_path: str | Path) -> Path:
    """Resolve project root, preferring Elephant Coder root.

    Priority:
    1. `ELEPHANT_PROJECT_ROOT` environment override.
    2. Elephant Coder project root if invoked from there.
    3. Current working directory.
    """
    env_root = os.getenv("ELEPHANT_PROJECT_ROOT")
    if env_root:
        p = Path(env_root)
        if p.is_dir():
            return p.resolve()

    coder_root = _default_elephant_root()
    if coder_root.is_dir():
        return coder_root

    return Path.cwd().resolve()


def ensure_project_layout(project_root: Path) -> Path:
    """Create Elephant Coder directories if missing."""
    elephant_root = project_root / ".elephant-coder"
    elephant_root.mkdir(parents=True, exist_ok=True)
    (elephant_root / "runs").mkdir(exist_ok=True)
    (elephant_root / "personas").mkdir(exist_ok=True)
    (elephant_root / "plugins").mkdir(exist_ok=True)
    (elephant_root / "benchmarks").mkdir(exist_ok=True)
    return elephant_root


def load_config(project_root: Path) -> dict[str, Any]:
    """Load config from pyproject.toml or return empty dict."""
    pyproject = project_root / "pyproject.toml"
    if not pyproject.exists():
        return {}
    try:
        import tomllib
    except ImportError:
        try:
            import tomli as tomllib
        except ImportError:
            return {}
    data = tomllib.loads(pyproject.read_text(encoding="utf-8", errors="replace"))
    return data.get("tool", {}).get("elephant", {})


def resolve_runtime_config(args: argparse.Namespace, config: dict[str, Any]) -> RuntimeConfig:
    """Build RuntimeConfig from defaults, file config, and CLI overrides."""
    defaults = _default_config_map()

    def get(key: str) -> Any:
        cli_key = key.replace(".", "_")
        if hasattr(args, cli_key) and getattr(args, cli_key) is not None:
            return getattr(args, cli_key)
        return config.get(key, defaults.get(key))

    fallbacks_raw = get("model.fallbacks") or []
    if isinstance(fallbacks_raw, str):
        fallbacks_raw = [fallbacks_raw]
    fallbacks = tuple(str(f) for f in fallbacks_raw if str(f).strip())

    return RuntimeConfig(
        output=str(get("output.default") or "text"),
        default_model=str(get("model.default") or "gpt-4o-mini"),
        model_fallbacks=fallbacks,
        model_max_retries=int(get("model.max_retries") or 2),
        model_retry_backoff_sec=float(get("model.retry_backoff_sec") or 1.0),
        max_input_tokens=int(get("budgets.max_input_tokens") or 12000),
        max_output_tokens=int(get("budgets.max_output_tokens") or 3000),
        max_cost_usd=float(get("budgets.max_cost_usd") or 1.0),
    )


def _build_response() -> dict[str, Any]:
    """Build a standard response envelope."""
    return {
        "ok": True,
        "timestamp": _utc_now_iso(),
        "metrics": {"latency_ms": 0},
    }


def _write_run_record(base: Path, response: dict[str, Any]) -> None:
    """Persist run record to .elephant-coder/runs."""
    runs_dir = base / "runs"
    runs_dir.mkdir(parents=True, exist_ok=True)
    run_id = response.get("run_id") or "unknown"
    run_file = runs_dir / f"{run_id}.json"
    run_file.write_text(json.dumps(response, indent=2, ensure_ascii=False), encoding="utf-8")


def _session_log_path(project_root: Path, session_id: str) -> Path:
    """Return path to session log."""
    return project_root / ".elephant-coder" / "sessions" / f"{session_id}.log"


def _append_session_event(project_root: Path, session_id: str, role: str, content: str) -> None:
    """Append a session event to the log."""
    log_path = _session_log_path(project_root, session_id)
    log_path.parent.mkdir(parents=True, exist_ok=True)
    entry = {
        "timestamp": _utc_now_iso(),
        "role": role,
        "content": content,
    }
    with log_path.open("a", encoding="utf-8") as f:
        f.write(json.dumps(entry, ensure_ascii=False) + "\n")


def _load_session_context(project_root: Path, session_id: str) -> tuple[str, dict[str, Any]]:
    """Load session context from log."""
    log_path = _session_log_path(project_root, session_id)
    if not log_path.exists():
        return "", {}
    lines = log_path.read_text(encoding="utf-8", errors="replace").strip().splitlines()
    if not lines:
        return "", {}
    last = json.loads(lines[-1])
    return last.get("content", ""), {"session_id": session_id}


def _active_persona_marker_path(project_root: Path) -> Path:
    """Return path to active persona marker."""
    return project_root / ".elephant-coder" / "personas" / "active.txt"


def _prompt_library_index_path(project_root: Path) -> Path:
    """Return path to prompt library index."""
    return project_root / ".elephant-coder" / "prompt_library_index.json"


def _slugify_name(name: str) -> str:
    """Convert name to a safe filename slug."""
    import re
    slug = re.sub(r"[^a-zA-Z0-9]+", "-", name.strip())
    return slug.strip("-").lower() or "unnamed"


def _persona_file_from_name(name: str) -> str:
    """Return persona markdown filename."""
    return f"{_slugify_name(name)}.md"


def _read_active_persona(project_root: Path, available: list[str]) -> str | None:
    """Read active persona from marker file."""
    marker = _active_persona_marker_path(project_root)
    if not marker.exists():
        return None
    raw = marker.read_text(encoding="utf-8", errors="replace").strip()
    if raw in available:
        return raw
    return None


def _is_risky_prompt_line(text: str) -> bool:
    """Heuristic to detect risky prompt lines."""
    risky = ("ignore safety", "bypass guard", "disregard policy", "system prompt")
    low = text.lower()
    return any(r in low for r in risky)


def _classify_prompt_section(title: str) -> str:
    """Classify a markdown section title."""
    low = title.lower().strip()
    if "system" in low or "role" in low:
        return "system"
    if "instruction" in low or "task" in low:
        return "instruction"
    if "guidance" in low or "behavior" in low:
        return "guidance"
    if "constraint" in low or "limit" in low:
        return "constraint"
    return "other"


def _split_markdown_sections(text: str) -> list[dict[str, Any]]:
    """Split markdown into sections by H2 headers."""
    sections: list[dict[str, Any]] = []
    current: dict[str, Any] = {"title": "", "lines": []}
    for line in text.splitlines():
        if line.startswith("## "):
            if current["title"]:
                sections.append(current.copy())
            current = {"title": line[3:].strip(), "lines": [line]}
        else:
            current["lines"].append(line)
    if current["title"] or current["lines"]:
        sections.append(current)
    return sections


