def load_config(project_root: Path) -> dict[str, Any]:
    """Load config from pyproject.toml or fallback to defaults."""
    config_map = _default_config_map()
    pyproject = project_root / "pyproject.toml"
    if not pyproject.exists():
        return config_map
    try:
        import tomllib
    except ImportError:
        try:
            import tomli as tomllib
        except ImportError:
            return config_map
    try:
        payload = tomllib.loads(pyproject.read_text(encoding="utf-8", errors="replace"))
    except Exception:
        return config_map
    tool = payload.get("tool", {})
    elephant = tool.get("elephant", {})
    if not isinstance(elephant, dict):
        return config_map
    # Merge flat keys
    for key, value in elephant.items():
        if isinstance(value, (str, int, float, bool, list)):
            config_map[key] = value
    return config_map


