{"summary": "Refactor persona parsing helpers to simplify classification and sanitization rules.", "project": {"language": "python"}, "files": [{"path": "scripts/elephant_cli.py", "content": "#!/usr/bin/env python3
"""Elephant Coder CLI scaffold (Phase 1).

This module provides the command router, response envelope, project-local
layout bootstrap, config loading, and placeholder command handlers.
"""

from __future__ import annotations

import argparse
import hashlib
import json
import os
import shutil
import shlex
import subprocess
import sys
import tempfile
import time
import uuid
from dataclasses import dataclass
from datetime import datetime, timezone
from pathlib import Path
from typing import Any

_SCRIPT_ROOT = Path(__file__).resolve().parents[1]

COMMANDS = ("plan", "code", "agents", "persona", "plugins", "git", "stats", "test", "session")
ERROR_CODES = {
    "E_CONFIG",
    "E_BUDGET",
    "E_MODEL",
    "E_INDEX",
    "E_IMPACT",
    "E_PLUGIN_PERMISSION",
    "E_STORAGE",
    "E_TEST",
}


@dataclass(frozen=True)
class RuntimeConfig:
    """Runtime configuration after defaults, file config, and CLI overrides."""

    output: str
    default_model: str
    model_fallbacks: tuple[str, ...]
    model_max_retries: int
    model_retry_backoff_sec: float
    max_input_tokens: int
    max_output_tokens: int
    max_cost_usd: float


class CLIError(RuntimeError):
    """Known CLI-level exception with stable error code."""

    def __init__(self, code: str, message: str):
        if code not in ERROR_CODES:
            raise ValueError(f"Unknown CLI error code: {code}")
        super().__init__(message)
        self.code = code
        self.message = message


def _utc_now_iso() -> str:
    """Return UTC timestamp in RFC3339-like format."""
    return datetime.now(timezone.utc).strftime("%Y-%m-%dT%H:%M:%SZ")


def _make_id(prefix: str) -> str:
    """Build a short deterministic-looking run/session ID."""
    return f"{prefix}_{uuid.uuid4().hex[:12]}"


def _normalize_argv(argv: list[str]) -> list[str]:
    """Map slash commands to argparse command names."""
    if not argv:
        return argv
    if argv[0].startswith("/") and len(argv[0]) > 1:
        argv = [argv[0][1:]] + argv[1:]
    return argv


def _parse_scalar(value: str) -> Any:
    """Parse scalar config values from text."""
    raw = value.strip()
    low = raw.lower()
    if low in {"true", "false"}:
        return low == "true"
    try:
        if "." in raw:
            return float(raw)
        return int(raw)
    except ValueError:
        pass
    if "," in raw:
        return [part.strip() for part in raw.split(",") if part.strip()]
    return raw


def _default_config_map() -> dict[str, Any]:
    """Return flat default config values."""
    return {
        "output.default": "text",
        "model.default": "gpt-4o-mini",
        "model.fallbacks": [],
        "model.max_retries": 2,
        "model.retry_backoff_sec": 1.0,
        "budgets.max_input_tokens": 12000,
        "budgets.max_output_tokens": 3000,
        "budgets.max_cost_usd": 1.0,
        "plugins.allowed_permissions": [],
    }


def _gguf_registry_path(project_root: Path) -> Path:
    """Return path to local GGUF specialist registry."""
    return project_root / ".elephant-coder" / "gguf_specialists.json"


def _default_gguf_registry() -> dict[str, Any]:
    """Return default GGUF specialist registry scaffold."""
    return {
        "version": "v1",
        "specialists": [],
    }


def _load_gguf_registry(project_root: Path) -> dict[str, Any]:
    """Load GGUF specialist registry from disk."""
    registry_path = _gguf_registry_path(project_root)
    if not registry_path.exists():
        return _default_gguf_registry()
    try:
        payload = json.loads(registry_path.read_text(encoding="utf-8", errors="replace"))
        if not isinstance(payload, dict):
            return _default_gguf_registry()
        return payload
    except (json.JSONDecodeError, OSError):
        return _default_gguf_registry()


def _specialist_ready(spec: dict[str, Any]) -> tuple[bool, str]:
    """Check if a specialist is ready for inference."""
    if not isinstance(spec, dict):
        return False, "invalid spec"
    gguf = spec.get("gguf")
    if not isinstance(gguf, str) or not gguf.strip():
        return False, "missing gguf path"
    gguf_path = Path(gguf)
    if not gguf_path.exists():
        return False, "gguf file not found"
    return True, ""


def _normalized_specialists(project_root: Path) -> list[dict[str, Any]]:
    """Return list of ready specialists."""
    registry = _load_gguf_registry(project_root)
    specialists = registry.get("specialists", [])
    if not isinstance(specialists, list):
        return []
    out: list[dict[str, Any]] = []
    for spec in specialists:
        ready, reason = _specialist_ready(spec)
        if ready:
            out.append(spec)
    return out


def _select_local_specialist(project_root: Path, task: str, requested: str | None) -> tuple[dict[str, Any] | None, str]:
    """Select a local specialist for the given task."""
    if requested:
        registry = _load_gguf_registry(project_root)
        specialists = registry.get("specialists", [])
        for spec in specialists:
            if spec.get("name") == requested:
                ready, reason = _specialist_ready(spec)
                if ready:
                    return spec, ""
                return None, reason
        return None, "requested specialist not found"

    specialists = _normalized_specialists(project_root)
    if not specialists:
        return None, "no ready specialists"
    return specialists[0], ""


def _route_for_code() -> tuple[dict[str, Any], list[str]]:
    """Return code command route configuration."""
    return {
        "command": "code",
        "description": "Generate code based on plan and context",
    }, []


def _messages_to_local_prompt(messages: list[dict[str, str]]) -> str:
    """Convert chat messages to a single prompt string."""
    parts: list[str] = []
    for msg in messages:
        role = msg.get("role", "unknown")
        content = msg.get("content", "")
        parts.append(f"{role.upper()}: {content}")
    return "\n\n".join(parts)


def _run_local_specialist_inference() -> tuple[str, dict[str, int], float]:
    """Run local specialist inference (placeholder)."""
    return "placeholder response", {"prompt_tokens": 0, "completion_tokens": 0, "total_tokens": 0}, 0.0


def _default_elephant_root() -> Path:
    """Return default Elephant project root."""
    return Path.home() / ".elephant"


def detect_project_root(start_path: str | Path) -> Path:
    """Walk upward to find a project root."""
    path = Path(start_path).resolve()
    for current in [path, *path.parents]:
        if (current / "pyproject.toml").exists():
            return current
        if (current / ".elephant-coder").exists():
            return current
    return path


def ensure_project_layout(project_root: Path) -> Path:
    """Create required directories and files for Elephant Coder."""
    dirs = [
        project_root / ".elephant-coder" / "personas",
        project_root / ".elephant-coder" / "sessions",
        project_root / ".elephant-coder" / "runs",
        project_root / ".elephant-coder" / "benchmarks",
    ]
    for d in dirs:
        d.mkdir(parents=True, exist_ok=True)
    return project_root


def load_config(project_root: Path) -> dict[str, Any]:
    """Load configuration from pyproject.toml or defaults."""
    config_path = project_root / "pyproject.toml"
    if not config_path.exists():
        return _default_config_map()
    try:
        import tomllib
    except ImportError:
        try:
            import tomli as tomllib
        except ImportError:
            return _default_config_map()

    try:
        data = tomllib.loads(config_path.read_text(encoding="utf-8", errors="replace"))
        return data.get("tool", {}).get("elephant", _default_config_map())
    except Exception:
        return _default_config_map()


def resolve_runtime_config(args: argparse.Namespace, config: dict[str, Any]) -> RuntimeConfig:
    """Build RuntimeConfig from CLI args and file config."""
    return RuntimeConfig(
        output=args.output or config.get("output.default", "text"),
        default_model=args.model or config.get("model.default", "gpt-4o-mini"),
        model_fallbacks=tuple(config.get("model.fallbacks", [])),
        model_max_retries=int(config.get("model.max_retries", 2)),
        model_retry_backoff_sec=float(config.get("model.retry_backoff_sec", 1.0)),
        max_input_tokens=int(config.get("budgets.max_input_tokens", 12000)),
        max_output_tokens=int(config.get("budgets.max_output_tokens", 3000)),
        max_cost_usd=float(config.get("budgets.max_cost_usd", 1.0)),
    )


def _build_response() -> dict[str, Any]:
    """Build standard response envelope."""
    return {
        "ok": True,
        "timestamp": _utc_now_iso(),
        "metrics": {},
    }


def _write_run_record(base: Path, response: dict[str, Any]) -> None:
    """Write run record to disk."""
    runs_dir = base / ".elephant-coder" / "runs"
    runs_dir.mkdir(parents=True, exist_ok=True)
    run_id = response.get("run_id", "unknown")
    run_file = runs_dir / f"{run_id}.json"
    run_file.write_text(json.dumps(response, indent=2, ensure_ascii=False), encoding="utf-8")


def _session_log_path(project_root: Path, session_id: str) -> Path:
    """Return path to session log."""
    safe = "".join(ch for ch in str(session_id).strip() if ch.isalnum() or ch in {"-", "_"})
    return project_root / ".elephant-coder" / "sessions" / f"{safe}.jsonl"


def _append_session_event(project_root: Path, session_id: str, role: str, content: str) -> None:
    """Append an event to the session log."""
    log_path = _session_log_path(project_root, session_id)
    log_path.parent.mkdir(parents=True, exist_ok=True)
    event = {
        "timestamp": _utc_now_iso(),
        "role": role,
        "content": content,
    }
    with log_path.open("a", encoding="utf-8") as f:
        f.write(json.dumps(event, ensure_ascii=False) + "\n")


def _load_session_context(project_root: Path, session_id: str) -> tuple[str, dict[str, Any]]:
    """Load session context from log."""
    log_path = _session_log_path(project_root, session_id)
    if not log_path.exists():
        return
