{"summary": "The /plan command currently does not persist the generated plan to PLAN.md. The fix involves ensuring the plan output is written to the project root's PLAN.md file after a successful /plan run. Based on the CLI structure and state store usage, the fix should be added in the plan command handler in elephant_cli.py.", "project": {"language": "python"}, "files": [{"path": "scripts/elephant_cli.py", "content": "#!/usr/bin/env python3
"""Elephant Coder CLI scaffold (Phase 1).

This module provides the command router, response envelope, project-local
layout bootstrap, config loading, and placeholder command handlers.
"""

from __future__ import annotations

import argparse
import hashlib
import json
import os
import shutil
import shlex
import subprocess
import sys
import tempfile
import time
import uuid
from dataclasses import dataclass
from datetime import datetime, timezone
from pathlib import Path
from typing import Any

_SCRIPT_ROOT = Path(__file__).resolve().parents[1]

COMMANDS = ("plan", "code", "agents", "persona", "plugins", "git", "stats", "test", "session")
ERROR_CODES = {
    "E_CONFIG",
    "E_BUDGET",
    "E_MODEL",
    "E_INDEX",
    "E_IMPACT",
    "E_PLUGIN_PERMISSION",
    "E_STORAGE",
    "E_TEST",
}


def _utc_now_iso() -> str:
    return datetime.now(timezone.utc).strftime("%Y-%m-%dT%H:%M:%SZ")


def _default_session_id() -> str:
    stamp = datetime.now(timezone.utc).strftime("%Y%m%dT%H%M%SZ")
    return f"sess_{stamp}_{os.getpid()}"


def _sanitize_id(raw: str) -> str:
    value = "".join(ch for ch in str(raw).strip() if ch.isalnum() or ch in {"-", "_"})
    if not value:
        raise ValueError("session id must contain at least one valid character")
    return value


def _session_log_path(project_root: Path, session_id: str) -> Path:
    safe = _sanitize_id(session_id)
    return project_root / ".elephant-coder" / "sessions" / f"{safe}.jsonl"


def _load_cli_module(project_root: Path):
    cli_path = project_root / ".elephant-coder" / "cli.py"
    if not cli_path.exists():
        return None
    spec = importlib.util.spec_from_file_location("elephant_cli_local", cli_path)
    if spec is None or spec.loader is None:
        return None
    mod = importlib.util.module_from_spec(spec)
    spec.loader.exec_module(mod)
    return mod


def _ensure_project_structure(project_root: Path) -> None:
    dirs = [
        project_root / ".elephant-coder",
        project_root / ".elephant-coder" / "sessions",
        project_root / ".elephant-coder" / "plugins",
    ]
    for d in dirs:
        d.mkdir(parents=True, exist_ok=True)


def _load_config(project_root: Path) -> dict[str, Any]:
    cfg_path = project_root / ".elephant-coder" / "config.json"
    if not cfg_path.exists():
        return {}
    try:
        return json.loads(cfg_path.read_text(encoding="utf-8"))
    except Exception:
        return {}


def _save_config(project_root: Path, config: dict[str, Any]) -> None:
    cfg_path = project_root / ".elephant-coder" / "config.json"
    cfg_path.write_text(json.dumps(config, indent=2) + "\n", encoding="utf-8")


def _log_session(project_root: Path, session_id: str, entry: dict[str, Any]) -> None:
    log_path = _session_log_path(project_root, session_id)
    log_path.parent.mkdir(parents=True, exist_ok=True)
    with log_path.open("a", encoding="utf-8") as f:
        f.write(json.dumps(entry) + "\n")


def _run_plan_command(project_root: Path, args: argparse.Namespace) -> int:
    """Execute the /plan command and persist PLAN.md."""
    from elephant_coder.index_engine import IndexService
    from elephant_coder.openrouter_client import OpenRouterClient
    from elephant_coder.state_store import StateStore

    config = _load_config(project_root)
    api_key = config.get("openrouter_api_key")
    if not api_key:
        print("ERROR: openrouter_api_key not configured", file=sys.stderr)
        return 1

    db_path = project_root / ".elephant-coder" / "index.db"
    index_service = IndexService(project_root)
    state_store = StateStore(db_path)

    try:
        # Refresh index if needed
        index_service.refresh_index()

        # Build prompt for plan generation
        files_meta = state_store.get_indexed_file_meta()
        files_list = list(files_meta.keys())
        if not files_list:
            print("No Python files indexed. Run /index first.", file=sys.stderr)
            return 1

        # Generate plan using OpenRouter
        client = OpenRouterClient(api_key)
        prompt = (
            "You are an expert software architect. Based on the following indexed Python files, "
            "generate a concise, actionable plan for the project. Output only the plan in markdown format.\n\n"
            "Files:\n" + "\n".join(f"- {p}" for p in files_list)
        )

        result = client.chat_completion()
        if result.error:
            print(f"ERROR: {result.error}", file=sys.stderr)
            return 1

        plan_content = result.content.strip()

        # Persist PLAN.md
        plan_path = project_root / "PLAN.md"
        plan_path.write_text(plan_content + "\n", encoding="utf-8")
        print(f"PLAN.md written ({len(plan_content)} bytes)")

        # Log session
        session_id = getattr(args, "session_id", _default_session_id())
        _log_session(
            project_root,
            session_id,
            {
                "timestamp": _utc_now_iso(),
                "command": "plan",
                "files_indexed": len(files_list),
                "plan_length": len(plan_content),
            },
        )

        return 0

    finally:
        index_service.close()


