"""Benchmark runner for Elephant Coder protocol v1."""

from __future__ import annotations

import argparse
import importlib.util
import json
import os
import subprocess
import sys
from dataclasses import dataclass
from datetime import datetime, timezone
from pathlib import Path
from statistics import mean
from typing import Any


def _utc_now_iso() -> str:
    return datetime.now(timezone.utc).strftime("%Y-%m-%dT%H:%M:%SZ")


def _default_benchmark_id() -> str:
    stamp = datetime.now(timezone.utc).strftime("%Y%m%dT%H%M%SZ")
    return f"bench_{stamp}_{os.getpid()}"


def _sanitize_id(raw: str) -> str:
    value = "".join(ch for ch in str(raw).strip() if ch.isalnum() or ch in {"-", "_"})
    if not value:
        raise ValueError("benchmark/session id must contain at least one valid character")
    return value


def _session_log_path(project_root: Path, session_id: str) -> Path:
    safe = _sanitize_id(session_id)
    return project_root / ".elephant-coder" / "sessions" / f"{safe}.jsonl"


def _load_cli_module(project_root: Path):
    cli_path = project_root / "scripts" / "elephant_cli.py"
    spec = importlib.util.spec_from_file_location("elephant_cli", cli_path)
    if spec is None or spec.loader is None:
        raise RuntimeError("elephant_cli.py not found")
    cli = importlib.util.module_from_spec(spec)
    spec.loader.exec_module(cli)
    return cli


def _run_cli_command(project_root: Path, args: list[str]) -> subprocess.CompletedProcess:
    env = os.environ.copy()
    env["PYTHONPATH"] = str(project_root)
    return subprocess.run(
        [sys.executable, str(project_root / "scripts" / "elephant_cli.py"), *args],
        cwd=project_root,
        capture_output=True,
        text=True,
        env=env,
        check=False,
    )


def _collect_benchmark_artifacts(project_root: Path) -> list[Path]:
    artifacts_dir = project_root / ".elephant-coder" / "artifacts"
    if not artifacts_dir.exists():
        return []
    return sorted(artifacts_dir.glob("*.json"))


def _load_benchmark_summary(artifact_path: Path) -> dict[str, Any]:
    with open(artifact_path, "r", encoding="utf-8") as f:
        return json.load(f)


def _aggregate_benchmark_summaries(artifacts: list[Path]) -> dict[str, Any]:
    summaries = []
    for artifact in artifacts:
        try:
            summary = _load_benchmark_summary(artifact)
            summaries.append(summary)
        except Exception:
            continue
    if not summaries:
        return {"count": 0, "runs": []}
    return {
        "count": len(summaries),
        "runs": summaries,
        "aggregated": {
            "avg_latency_ms": mean(s.get("latency_ms", 0) for s in summaries),
            "avg_tokens": mean(s.get("tokens", 0) for s in summaries),
            "avg_cost_usd": mean(s.get("cost_usd", 0.0) for s in summaries),
        },
    }


def _stats_command(project_root: Path) -> dict[str, Any]:
    cli = _load_cli_module(project_root)
    base_stats = cli.index_stats(project_root)
    artifacts = _collect_benchmark_artifacts(project_root)
    benchmark_summary = _aggregate_benchmark_summaries(artifacts)
    base_stats["benchmark_summary"] = benchmark_summary
    return base_stats


def main() -> None:
    parser = argparse.ArgumentParser(description="Elephant Coder benchmark runner")
    parser.add_argument("--project-root", type=Path, default=Path.cwd(), help="Project root directory")
    parser.add_argument("--session-id", type=str, default=_default_benchmark_id(), help="Benchmark session ID")
    parser.add_argument("--command", type=str, default="index", help="CLI command to benchmark")
    parser.add_argument("--iterations", type=int, default=3, help="Number of benchmark iterations")
    parser.add_argument("--output-dir", type=Path, default=None, help="Output directory for artifacts")
    args = parser.parse_args()

    project_root = args.project_root.resolve()
    session_id = _sanitize_id(args.session_id)
    output_dir = args.output_dir or (project_root / ".elephant-coder" / "artifacts")
    output_dir.mkdir(parents=True, exist_ok=True)

    cli = _load_cli_module(project_root)
    stats = cli.index_stats(project_root)
    print(json.dumps(stats, indent=2, ensure_ascii=False))


if __name__ == "__main__":
    main()
