}


# --- Refactored dispatch internals ---

@dataclass
class CommandResult:
    """Standardized result envelope for command execution."""
    ok: bool
    error_code: str | None = None
    message: str | None = None
    details: dict[str, Any] | None = None
    runtime_ms: float | None = None


class CommandDispatch:
    """Centralized command dispatch with unified warning/metadata handling."""

    def __init__(self, project_root: Path):
        self.project_root = project_root
        self.start_time = time.perf_counter()

    def _now(self) -> float:
        return time.perf_counter() - self.start_time

    def _warn(self, code: str, message: str) -> CommandResult:
        return CommandResult(
            ok=False,
            error_code=code,
            message=message,
            runtime_ms=self._now() * 1000,
        )

    def _ok(self, message: str | None = None, details: dict[str, Any] | None = None) -> CommandResult:
        return CommandResult(
            ok=True,
            message=message,
            details=details,
            runtime_ms=self._now() * 1000,
        )

    def _error(self, code: str, message: str, details: dict[str, Any] | None = None) -> CommandResult:
        return CommandResult(
            ok=False,
            error_code=code,
            message=message,
            details=details,
            runtime_ms=self._now() * 1000,
        )

    def dispatch(self, cmd: str, args: argparse.Namespace) -> CommandResult:
        """Route command to handler and normalize result."""
        handler = getattr(self, f"cmd_{cmd}", None)
        if handler is None:
            return self._error("E_CMD", f"Unknown command: {cmd}")
        try:
            return handler(args)
        except Exception as e:
            return self._error("E_INTERNAL", f"Command '{cmd}' failed: {e}")

    # --- Command handlers ---

    def cmd_plan(self, args: argparse.Namespace) -> CommandResult:
        return self._warn("E_NOT_IMPL", "Command 'plan' not yet implemented")

    def cmd_code(self, args: argparse.Namespace) -> CommandResult:
        return self._warn("E_NOT_IMPL", "Command 'code' not yet implemented")

    def cmd_agents(self, args: argparse.Namespace) -> CommandResult:
        return self._warn("E_NOT_IMPL", "Command 'agents' not yet implemented")

    def cmd_persona(self, args: argparse.Namespace) -> CommandResult:
        return self._warn("E_NOT_IMPL", "Command 'persona' not yet implemented")

    def cmd_plugins(self, args: argparse.Namespace) -> CommandResult:
        return self._warn("E_NOT_IMPL", "Command 'plugins' not yet implemented")

    def cmd_git(self, args: argparse.Namespace) -> CommandResult:
        return self._warn("E_NOT_IMPL", "Command 'git' not yet implemented")

    def cmd_stats(self, args: argparse.Namespace) -> CommandResult:
        return self._warn("E_NOT_IMPL", "Command 'stats' not yet implemented")

    def cmd_test(self, args: argparse.Namespace) -> CommandResult:
        return self._warn("E_NOT_IMPL", "Command 'test' not yet implemented")

    def cmd_session(self, args: argparse.Namespace) -> CommandResult:
        return self._warn("E_NOT_IMPL", "Command 'session' not yet implemented")


# --- CLI entrypoint ---

def _build_parser() -> argparse.ArgumentParser:
    parser = argparse.ArgumentParser(prog="elephant", description="Elephant Coder CLI")
    subparsers = parser.add_subparsers(dest="command", required=True)
    for name in COMMANDS:
        subparsers.add_parser(name)
    return parser


def main() -> int:
    parser = _build_parser()
    args = parser.parse_args()
    dispatch = CommandDispatch(_SCRIPT_ROOT)
    result = dispatch.dispatch(args.command, args)
    output = {
        "ok": result.ok,
        "runtime_ms": result.runtime_ms,
    }
    if result.error_code:
        output["error_code"] = result.error_code
    if result.message:
        output["message"] = result.message
    if result.details:
        output["details"] = result.details
    print(json.dumps(output, indent=2))
    return 0 if result.ok else 1


if __name__ == "__main__":
    sys.exit(main())
