from __future__ import annotations

from dataclasses import dataclass
from pathlib import Path
from typing import Callable


@dataclass
class ContextProfile:
    """Represents a context selection strategy."""
    name: str
    description: str
    selector: Callable[[list[str], Path], list[str]]


def _select_by_git_diff(changed_files: list[str], project_root: Path) -> list[str]:
    """Select files based on git diff (placeholder for future implementation)."""
    return changed_files


def _select_by_affected_symbols(changed_files: list[str], project_root: Path) -> list[str]:
    """Select files based on symbol dependencies (placeholder for future implementation)."""
    return changed_files


def _select_by_import_tree(changed_files: list[str], project_root: Path) -> list[str]:
    """Select files based on import tree traversal."""
    return changed_files


def _select_all(changed_files: list[str], project_root: Path) -> list[str]:
    """Select all files for full context."""
    return changed_files


# Available context profiles
PROFILES = {
    "minimal": ContextProfile(
        name="minimal",
        description="Use only directly changed files.",
        selector=_select_by_git_diff,
    ),
    "symbols": ContextProfile(
        name="symbols",
        description="Include files with affected symbol definitions.",
        selector=_select_by_affected_symbols,
    ),
    "imports": ContextProfile(
        name="imports",
        description="Include files in the import dependency tree.",
        selector=_select_by_import_tree,
    ),
    "full": ContextProfile(
        name="full",
        description="Include all indexed files.",
        selector=_select_all,
    ),
}


def select_context(profile_name: str, changed_files: list[str], project_root: Path) -> list[str]:
    """Select context files using the specified profile."""
    profile = PROFILES.get(profile_name)
    if profile is None:
        raise ValueError(f"Unknown context profile: {profile_name}")
    return profile.selector(changed_files, project_root)
