"""Benchmark runner for Elephant Coder protocol v1."""

from __future__ import annotations

import argparse
import importlib.util
import json
import os
import subprocess
import sys
from dataclasses import dataclass
from datetime import datetime, timezone
from pathlib import Path
from statistics import mean
from typing import Any


def _utc_now_iso() -> str:
    return datetime.now(timezone.utc).strftime("%Y-%m-%dT%H:%M:%SZ")


def _default_benchmark_id() -> str:
    stamp = datetime.now(timezone.utc).strftime("%Y%m%dT%H%M%SZ")
    return f"bench_{stamp}_{os.getpid()}"


def _sanitize_id(raw: str) -> str:
    value = "".join(ch for ch in str(raw).strip() if ch.isalnum() or ch in {"-", "_"})
    if not value:
        raise ValueError("benchmark/session id must contain at least one valid character")
    return value


def _session_log_path(project_root: Path, session_id: str) -> Path:
    safe = _sanitize_id(session_id)
    return project_root / ".elephant-coder" / "sessions" / f"{safe}.jsonl"


def _load_cli_module(project_root: Path):
    cli_path = project_root / "scripts" / "elephant_cli.py"
    spec = importlib.util.spec_from_file_location("elephant_cli_benchmark", cli_path)
    if spec is None or spec.loader is None:
        raise RuntimeError(f"Unable to load CLI from {cli_path}")
    module = importlib.util.module_from_spec(spec)
    sys.modules[spec.name] = module
    spec.loader.exec_module(module)
    return module


@dataclass
class BenchmarkTask:
    task_id: str
    category: str
    prompt: str
    checks: list[str]
    expected_files: list[str]


def _load_tasks(tasks_path: Path) -> list[BenchmarkTask]:
    if not tasks_path.exists():
        raise FileNotFoundError(
            f"Task file not found: {tasks_path}. "
            "Create it from .benchmarks/tasks_v1.json template."
        )
    payload = json.loads(tasks_path.read_text(encoding="utf-8", errors="replace"))
    raw_tasks = payload.get("tasks", [])
    if not isinstance(raw_tasks, list):
        raise ValueError("tasks must be a list")
    out: list[BenchmarkTask] = []
    for item in raw_tasks:
        if not isinstance(item, dict):
            continue
        task_id = str(item.get("task_id", "")).strip()
        prompt = str(item.get("prompt", "")).strip()
        if not task_id or not prompt:
            continue
        category = str(item.get("category", "unspecified")).strip() or "unspecified"
        checks_raw = item.get("checks", [])
        checks = [str(cmd).strip() for cmd in checks_raw if str(cmd).strip()] if isinstance(checks_raw, list) else []
        exp_raw = item.get("expected_files", [])
        expected_files = [str(path).strip() for path in exp_raw if str(path).strip()] if isinstance(exp_raw, list) else []
        out.append(
            BenchmarkTask(
                task_id=task_id,
                category=category,
                prompt=prompt,
                checks=checks,
                expected_files=expected_files,
            )
        )
    if not out:
        raise ValueError(f"No valid tasks in {tasks_path}")
    return out


def _run_checks(project_root: Path, checks: list[str]) -> tuple[list[str], list[str]]:
    passed: list[str] = []
    failed: list[str] = []
    for cmd in checks:
        result = subprocess.run(
            cmd,
            shell=True,
            cwd=str(project_root),
            capture_output=True,
            text=True,
            timeout=60,
        )
        if result.returncode == 0:
            passed.append(cmd)
        else:
            failed.append(cmd)
    return passed, failed


def _summarize_mode(rows: list[dict[str, Any]]) -> dict[str, Any]:
    if not rows:
        return {}
    keys = {k for r in rows for k in r.keys() if isinstance(r[k], (int, float))}
    summary: dict[str, Any] = {}
    for k in keys:
        values = [r[k] for r in rows if isinstance(r.get(k), (int, float))]
        if values:
            summary[f"{k}_mean"] = round(mean(values), 2)
            summary[f"{k}_min"] = min(values)
            summary[f"{k}_max"] = max(values)
    return summary


def _build_report_md(results: dict[str, Any]) -> str:
    lines = ["# Benchmark Report", ""]
    meta = results.get("metadata", {})
    lines.append(f"- benchmark_id: {meta.get('benchmark_id', 'N/A')}")
    lines.append(f"- generated_at_utc: {meta.get('generated_at_utc', 'N/A')}")
    lines.append(f"- protocol_version: {meta.get('protocol_version', 'N/A')}")
    lines.append(f"- comparison_track: {meta.get('comparison_track', 'N/A')}")
    lines.append("")

    summary = results.get("summary", {})
    if summary:
        lines.append("## Summary")
        for k, v in summary.items():
            lines.append(f"- {k}: {v}")
        lines.append("")

    details = results.get("results", [])
    if details:
        lines.append("## Results")
        for item in details:
            lines.append(f"### {item.get('task_id', 'N/A')}")
            lines.append(f"- success: {item.get('success', False)}")
            lines.append(f"- latency_ms: {item.get('latency_ms', 0)}")
            lines.append(f"- total_tokens: {item.get('total_tokens', 0)}")
            lines.append(f"- cost_usd: {item.get('cost_usd', 0.0)}")
            lines.append("")

    return "\n".join(lines)


def _collect_run_metrics(run_payloads: list[dict[str, Any]]) -> dict[str, Any]:
    if not run_payloads:
        return {}
    tokens = [p.get("metrics", {}).get("total_tokens", 0) for p in run_payloads]
    latencies = [p.get("metrics", {}).get("latency_ms", 0) for p in run_payloads]
    costs = [p.get("metrics", {}).get("cost_usd", 0.0) for p in run_payloads]
    successes = [1 if p.get("ok", False) else 0 for p in run_payloads]
    return {
        "runs": len(run_payloads),
        "success_rate": round((sum(successes) / len(successes)) * 100.0, 2) if successes else 0.0,
        "avg_total_tokens": round(mean(tokens), 2) if tokens else 0.0,
        "avg_latency_ms": round(mean(latencies), 2) if latencies else 0.0,
        "total_cost_usd": round(sum(costs), 6) if costs else 0.0,
    }


def main() -> int:
    parser = argparse.ArgumentParser(description="Run benchmarks for Elephant Coder")
    parser.add_argument("--cwd", type=str, default=".", help="Project root directory")
    parser.add_argument("--tasks", type=str, default=".benchmarks/tasks_v1.json", help="Path to tasks file")
    parser.add_argument("--runs", type=int, default=1, help="Number of runs per task")
    parser.add_argument("--output", type=str, default="report.md", help="Output report path")
    args = parser.parse_args()

    project_root = Path(args.cwd).resolve()
    tasks_path = project_root / args.tasks
    tasks = _load_tasks(tasks_path)

    cli_module = _load_cli_module(project_root)
    results: list[dict[str, Any]] = []
    run_payloads: list[dict[str, Any]] = []

    for task in tasks:
        for _ in range(args.runs):
            payload = {
                "command": "code",
                "args": ["--cwd", str(project_root), "--task", task.prompt, "--output", "json"],
            }
            # Simulate CLI execution
            try:
                code, response = cli_module.execute_command(
                    cli_module._parse(
                        cli_module._build_parser(),
                        ["code", "--cwd", str(project_root), "--task", task.prompt, "--output", "json"],
                    )
                )
                run_payloads.append(response)
                success = code == 0
                metrics = response.get("metrics", {})
                results.append(
                    {
                        "task_id": task.task_id,
                        "success": success,
                        "latency_ms": metrics.get("latency_ms", 0),
                        "total_tokens": metrics.get("total_tokens", 0),
                        "cost_usd": metrics.get("cost_usd", 0.0),
                    }
                )
            except Exception as e:
                results.append(
                    {
                        "task_id": task.task_id,
                        "success": False,
                        "latency_ms": 0,
                        "total_tokens": 0,
                        "cost_usd": 0.0,
                        "error": str(e),
                    }
                )

    baseline_metrics = _collect_run_metrics(run_payloads)
    elephant_metrics = _summarize_mode(results)

    report = {
        "metadata": {
            "generated_at_utc": _utc_now_iso(),
            "benchmark_id": _default_benchmark_id(),
            "protocol_version": "v1",
            "comparison_track": "prompt-parity",
        },
        "summary": {
            "tasks_total": len(tasks),
            "runs_total": len(run_payloads),
            "baseline": baseline_metrics,
            "elephant": elephant_metrics,
        },
        "results": results,
    }

    report_md = _build_report_md(report)
    (project_root / args.output).write_text(report_md, encoding="utf-8")
    print(f"Report written to {args.output}")
    return 0


if __name__ == "__main__":
    sys.exit(main())
